UPDATE `locales_quest` SET `title_loc9`='Ritorno da Billy',`details_loc9`='Ecco qua. Quando consegnerai questo pasticcio a Billy, digli che spero gli vada di traverso!',`objectives_loc9`='Porta il Pasticcio di Carne di Cinghiale a Billy Maclure presso le Vigne dei Maclure.' WHERE `entry`=84;
