UPDATE `locales_quest` SET `title_loc9`='La fuga',`details_loc9`='Suppongo che impareremo strada facendo... Non può essere poi tanto difficile. Qualche bottone qua, una leva o due... Vogliamo andare?' WHERE `entry`=863;
