UPDATE `locales_quest` SET `title_loc9`='Samoflago',`details_loc9`='<Le tre valvole di controllo sono aperte. Le diverse rilevazioni del pannello di controllo ti fanno pensare che debbano essere chiuse per poter disattivare il dispositivo.>',`objectives_loc9`='Chiudi la Valvola di Controllo del Carburante, la Valvola di Regolazione e la Valvola di Controllo Principale. Quindi usa di nuovo la console di comando.' WHERE `entry`=900;
UPDATE `locales_quest` SET `title_loc9`='Samoflago',`details_loc9`='<Ora che il dispositivo è disattivato, puoi aprire il pannello di controllo e sganciare dalla console il Samoflago. Non resta altro che ottenere la chiave per aprire la console.>' WHERE `entry`=901;
UPDATE `locales_quest` SET `title_loc9`='Samoflago',`details_loc9`='<Aprendo il pannello della console di comando, ti trovi di fronte un intrico di cavi, tubi e altri strani oggetti meccanici, la maggior parte dei quali sembra non avere uno scopo pratico. Spostandoli ed esaminando più a fondo la console, trovi il Samoflago. Tiri leggermente e si sgancia.>' WHERE `entry`=902;
