UPDATE `locales_quest` SET `title_loc9`='Il Cuore di Hakkar',`details_loc9`='Il Cuore di Hakkar brucia dall\'interno. Anche se lo Scortica Anime è sconfitto, il nucleo del suo potere è ancora maledetto.',`objectives_loc9`='Porta il Cuore di Hakkar a Molthor sull\'Isola di Yojamba.' WHERE `entry`=8183;
