UPDATE `locales_quest` SET `title_loc1`='검은바위의 군주',`details_loc1`='영웅의 계곡으로 가서 야전사령관 아프라샤비와 얘기하십시오.',`objectives_loc1`='스톰윈드에 있는 영웅의 계곡으로 가서 야전사령관 아프라샤비와 대화해야 합니다.' WHERE `entry`=7782;
