UPDATE `locales_quest` SET `title_loc1`='셴드랄라의 보물',`details_loc1`='도서관에 가면 계단 밑에 오래된 궤짝이 하나 있는데, 그 안에서 원하는 걸 골라가세요.',`objectives_loc1`='도서관으로 되돌아가 셴드랄라의 보물을 찾아야 합니다. 임무 완수에 대한 보상을 받으십시오!' WHERE `entry`=7877;
