UPDATE `locales_quest` SET `title_loc1`='겨울 할아버지의 선물',`objectives_loc1`='겨울 할아버지와 대화해야 합니다. 아이언포지의 굴뚝나무 목장조합 상인 근처로 가면 만날 수 있을 것입니다.' WHERE `entry`=8827;
