UPDATE `trans_words` SET `content_loc4`='Elkay\'gan the Mystic at Garadar in Nagrand wants you to use the Mag\'har Battle Standard at the First Burning Blade Pyre, Second Burning Blade Pyre, and Third Burning Blade Pyre. Return the left-over Mag\'har Battle Standards to him when you are finished.' WHERE `word_id` = 148895; -- locales_quest - objectives : entry=9910
UPDATE `trans_words` SET `content_loc4`='Elune be praised! The restless spirits may find tranquility after all.' WHERE `word_id` = 147283; -- locales_quest - offerrewardtext : entry=9513
