UPDATE `trans_words` SET `content_loc4`='Take the Dark Iron Ale Keg to Tapper Swindlekeg in Durotar.' WHERE `word_id` = 154385; -- locales_quest - objectives : entry=11487
UPDATE `trans_words` SET `content_loc4`='Take the Fel Reaver Construction Manual to Mama Wheeler at the Midrealm Post inside Eco-Dome Midrealm in the Netherstorm.' WHERE `word_id` = 150255; -- locales_quest - objectives : entry=10244
UPDATE `trans_words` SET `content_loc4`='Take the Feralfen Totem to the foot of the stairs at the Boha\'mu Ruins and plant it in the ground. Report your findings to Seer Janidi at Zabra\'jin.' WHERE `word_id` = 148646; -- locales_quest - objectives : entry=9847
UPDATE `trans_words` SET `content_loc4`='Take the Filled Shimmering Vessel to Silvermoon City\'s inn and use its magic to resurrect Sangrias Stillblade.' WHERE `word_id` = 147941; -- locales_quest - objectives : entry=9685
UPDATE `trans_words` SET `content_loc4`='Take the Flask of Purest Water to Farseer Nobundo inside the Exodar on Azuremyst Isle.' WHERE `word_id` = 147268; -- locales_quest - objectives : entry=9509
UPDATE `trans_words` SET `content_loc4`='Take the information that you beat out of Unkor back to Kilrath in Terokkar Forest.' WHERE `word_id` = 148812; -- locales_quest - objectives : entry=9890
UPDATE `trans_words` SET `content_loc4`='Take the information that you beat out of Unkor to Jorin Deadeye at Garadar in Nagrand.' WHERE `word_id` = 148816; -- locales_quest - objectives : entry=9891
UPDATE `trans_words` SET `content_loc4`='Take the Kurken\'s hide to Moordo, on the other side of the village. He will fashion the hide into an item of your choice.' WHERE `word_id` = 272523; -- locales_quest - details : entry=9571
UPDATE `trans_words` SET `content_loc4`='Take the Legion Missive to Force Commander Danath Trollbane at Honor Hold in Hellfire Peninsula.' WHERE `word_id` = 149844; -- locales_quest - objectives : entry=10148
UPDATE `trans_words` SET `content_loc4`='Take the Legion Missive to Nazgrel at Thrallmar in Hellfire Peninsula.' WHERE `word_id` = 149792; -- locales_quest - objectives : entry=10135
UPDATE `trans_words` SET `content_loc4`='Take the Letter of Introduction to Huntress Kella Nightbow.' WHERE `word_id` = 147771; -- locales_quest - objectives : entry=9632
UPDATE `trans_words` SET `content_loc4`='Take the Missive to Warp-Scryer Kryv in the main keep of Honor Hold.' WHERE `word_id` = 150864; -- locales_quest - objectives : entry=10395
UPDATE `trans_words` SET `content_loc4`='Take the Murkblood Invasion Plans to Arechron.' WHERE `word_id` = 148754; -- locales_quest - objectives : entry=9871
UPDATE `trans_words` SET `content_loc4`='Take the Ominous Letter to Nazgrel at Thrallmar in Hellfire Peninsula.' WHERE `word_id` = 147596; -- locales_quest - objectives : entry=9588
UPDATE `trans_words` SET `content_loc4`='Take the Rune Covered Tablet to Priestess Kyleen Il\'dinare at Odesyus\' Landing on Azuremyst Isle.' WHERE `word_id` = 272474; -- locales_quest - objectives : entry=9514
UPDATE `trans_words` SET `content_loc4`='Take the Tome of Thomas Thomson to a Masked Orphan Matron in Durotar, Brill or Falconwing Square.' WHERE `word_id` = 154308; -- locales_quest - objectives : entry=11403
UPDATE `trans_words` SET `content_loc4`='Take the Tome of Thomas Thomson to the Costumed Orphan Matron in Goldshire, Kharanos or Azure Watch.' WHERE `word_id` = 154096; -- locales_quest - objectives : entry=11242
UPDATE `trans_words` SET `content_loc4`='Take the ward with you and travel to Moonglade, to the Stormrage Barrow Dens. Within the southernmost of the Stormrage Barrow Dens, watched over by the dreamwardens, you will find Clintar Dreamwalker.$B$BAdminister the potion and watch over him while he wakes. He\'ll be extremely vulnerable as his spirit reunites with his body and he certainly won\'t recognize you. Declare yourself to him as soon as he comes around, $N. He will need your help to gather the relics for the ceremony.' WHERE `word_id` = 153097; -- locales_quest - details : entry=10964
UPDATE `trans_words` SET `content_loc4`='Take the Withered Basidium to Ruam in Telredor.' WHERE `word_id` = 148555; -- locales_quest - objectives : entry=9827
UPDATE `trans_words` SET `content_loc4`='Take the Zeppelin Cargo to Drazzit Dripvalve at Mudsprocket.' WHERE `word_id` = 154041; -- locales_quest - objectives : entry=11208
UPDATE `trans_words` SET `content_loc4`='Take this box to Kravel.' WHERE `word_id` = 145648; -- locales_quest - objectives : entry=3482
UPDATE `trans_words` SET `content_loc4`='Take this draught, $N. After you drink it, your physical strength against the Scourge will receive a boost. If you need more than this sample bring me more hearts.' WHERE `word_id` = 146495; -- locales_quest - offerrewardtext : entry=9216
UPDATE `trans_words` SET `content_loc4`='Take this vessel, $N. The enchantment it bears will allow you to draw the last vestiges of the Light surrounding these magisters. They once tended the captive naaru and some of its energy yet remains within them.$B$BIt will be just enough to restore your comrade to life.$B$B.Do not worry about the magisters, $N. I have seen to their care, and Lord Solanar assures me that Lady Liadrin seeks a new source of power even now.' WHERE `word_id` = 147935; -- locales_quest - details : entry=9684
UPDATE `trans_words` SET `content_loc4`='Take this, $N. You\'ll feel the difference immediately!$B$BFeel free to bring more spinal dust; I can use as much of it as you can provide. Side effects?  You might start feeling your joints acting up a bit. Nothing permanent, I assure you!' WHERE `word_id` = 146502; -- locales_quest - offerrewardtext : entry=9218
UPDATE `trans_words` SET `content_loc4`='Take Topher\'s List to Laando, the Hippogryph Master at Blood Watch.' WHERE `word_id` = 147657; -- locales_quest - objectives : entry=9603
UPDATE `trans_words` SET `content_loc4`='Take what you have learned and tell Jorin at Garadar. I can assure you that he won\'t be happy to know that our village was destroyed and many of our people killed because the ogres needed more space.$B$BMay the ancestors watch over you... I will be hiding in the bushes if you need me for anything else.' WHERE `word_id` = 148815; -- locales_quest - details : entry=9891
UPDATE `trans_words` SET `content_loc4`='Take your orphan, Salandria, to Sporeggar in Zangarmarsh. Make sure to call for her if she is not present when you arrive. Then, speak with Hch\'uu.' WHERE `word_id` = 153018; -- locales_quest - objectives : entry=10945
UPDATE `trans_words` SET `content_loc4`='Take your orphan, Salandria, to stand before Zaladormu, the dragon on the dais in the middle of the Caverns of Time. Make sure to call for her if she is not present when you arrive.$B$BThen, purchase a Toy Dragon for her from the Keepers of Time Quartermaster, Alurmi, near the bottom of the entrance tunnel.' WHERE `word_id` = 153092; -- locales_quest - objectives : entry=10963
UPDATE `trans_words` SET `content_loc4`='Take your orphan, Salandria, to the paladin trainers in Silvermoon City\'s Farstriders\' Square. Make sure to call for her if she is not present when you arrive. Then, speak with Lady Liadrin, the Blood Knight Matriarch.$B$BRemember that you can use your map inside the city, and speak to the guards to find the paladin trainers.' WHERE `word_id` = 153078; -- locales_quest - objectives : entry=10960
UPDATE `trans_words` SET `content_loc4`='Take your orphan, Salandria, to the Throne of the Elements in Nagrand. Make sure to call for her if she is not present when you arrive. Then, speak with Elementalist Sharvak.' WHERE `word_id` = 153051; -- locales_quest - objectives : entry=10953
UPDATE `trans_words` SET `content_loc4`='Take your orphan, Salandria, up the steps of the Stair of Destiny to stand before the Dark Portal in Hellfire Peninsula. Make sure to call for her if she is not present when you arrive.' WHERE `word_id` = 153044; -- locales_quest - objectives : entry=10951
UPDATE `trans_words` SET `content_loc4`='Talbuk Tagged' WHERE `word_id` = 151006; -- locales_quest - ObjectiveText2 : entry=10427
UPDATE `trans_words` SET `content_loc4`='Talk to Knight-Lord Bloodvalor to learn how to summon your Thalassian Warhorse.' WHERE `word_id` = 148063; -- locales_quest - objectives : entry=9712
UPDATE `trans_words` SET `content_loc4`='Talk to Nat Pagle at Nat\'s Landing in Tidefury Cove.' WHERE `word_id` = 153997; -- locales_quest - objectives : entry=11194
UPDATE `trans_words` SET `content_loc4`='Tame a Barbed Crawler' WHERE `word_id` = 147612; -- locales_quest - EndText : entry=9591
UPDATE `trans_words` SET `content_loc4`='Tame a Crazed Dragonhawk' WHERE `word_id` = 147173; -- locales_quest - EndText : entry=9484
UPDATE `trans_words` SET `content_loc4`='Tame a Greater Timberstrider' WHERE `word_id` = 147617; -- locales_quest - EndText : entry=9592
UPDATE `trans_words` SET `content_loc4`='Tame a Mistbat' WHERE `word_id` = 147178; -- locales_quest - EndText : entry=9485
UPDATE `trans_words` SET `content_loc4`='Tame a Nightstalker' WHERE `word_id` = 147622; -- locales_quest - EndText : entry=9593
UPDATE `trans_words` SET `content_loc4`='Tame an Elder Springpaw' WHERE `word_id` = 147183; -- locales_quest - EndText : entry=9486
UPDATE `trans_words` SET `content_loc4`='Tavara, one of our herbalists, has been gone far too long in her search for herbs. I fear she is hurt and in need of help, yet I cannot look for her myself.$B$BPlease find her and lend your healing if needed, fellow $C. She was heading east towards some hot springs when she left Azure Watch.' WHERE `word_id` = 147585; -- locales_quest - details : entry=9586
UPDATE `trans_words` SET `content_loc4`='Tempest Keep is a naaru creation, mortal. Access to the vessel has always been within our power to grant. A return to Tempest Keep, though, had been deemed unnecessary. A point of pride for a mortal race, perhaps, but naaru invest little in such things.$B$BWe go now because we must. Kael\'thas cannot be allowed to utter the words of damnation.$B$BWhile the way is open, only a fool would enter unproven. Do not underestimate the purpose of the trials, mortal.' WHERE `word_id` = 335477; -- locales_quest - offerrewardtext : entry=10883
UPDATE `trans_words` SET `content_loc4`='Terokk\'s perch, Skettis, looms above the Bone Wastes to the east. At the base of Skettis lies Veil Shalas, the most formidable of Terokk\'s colonies. It is through the stronghold of Veil Shalas that Terokk\'s forward Sages wage war against the Light.$B$BThe evil-doers of Shalas communicate with their master using a complex system of magical signal fires. Travel to the east, $R, and extinguish the fires. Once Veil Shalas is silenced, we can begin to plot the fall of Skettis.$B$BBut I get ahead of myself.' WHERE `word_id` = 152718; -- locales_quest - details : entry=10874
UPDATE `trans_words` SET `content_loc4`='Test Energy Modulator' WHERE `word_id` = 151000; -- locales_quest - ObjectiveText1 : entry=10426
UPDATE `trans_words` SET `content_loc4`='Test Tally\'s Experiment' WHERE `word_id` = 151463; -- locales_quest - EndText : entry=10557
UPDATE `trans_words` SET `content_loc4`='Test the first wand' WHERE `word_id` = 151504; -- locales_quest - ObjectiveText1 : entry=10566
UPDATE `trans_words` SET `content_loc4`='Test the fourth wand' WHERE `word_id` = 151507; -- locales_quest - ObjectiveText4 : entry=10566
UPDATE `trans_words` SET `content_loc4`='Test the second wand' WHERE `word_id` = 151505; -- locales_quest - ObjectiveText2 : entry=10566
UPDATE `trans_words` SET `content_loc4`='Thank Cenarius you showed up. My injuries prevented me from going back to the outside for help.' WHERE `word_id` = 148777; -- locales_quest - offerrewardtext : entry=9876
UPDATE `trans_words` SET `content_loc4`='Thank goodness for you!$B$B$C, I need you to help me escape. I have information vital to the security of Stonebreaker Hold and probably all of Outland!$B$BI came here from the Cenarion Expedition on a diplomatic mission, but as you can see, they weren\'t very friendly. If you help me get to safety, I can assure you that you will be handsomely rewarded!$B$BWe won\'t be able to do this on our own - you do have a group with you, right?' WHERE `word_id` = 149470; -- locales_quest - details : entry=10052
UPDATE `trans_words` SET `content_loc4`='Thank goodness for you!$B$B$C, I need you to help me escape. I have information vital to the security of the Allerian Stronghold and probably all of Outland!$B$BI came here from the Cenarion Expedition on a diplomatic mission, but as you can see, they weren\'t very friendly. If you help me get to safety, I can assure you that you will be handsomely rewarded!$B$BWe won\'t be able to do this on our own - you do have a group with you, right?' WHERE `word_id` = 149466; -- locales_quest - details : entry=10051
UPDATE `trans_words` SET `content_loc4`='Thank goodness for you, $C! Those slimy Grimscale murlocs have pirated away my cargo! It\'s bad enough that the Wretched have stolen what little magical goods I had, but the murlocs took the rest! What has this land come to?$B$BCould you retrieve my cargo? I can\'t expect that you\'d be able to get all of it back, but I\'ll be grateful for any of it at this point. It was the Grimscale murlocs in the huts, just to the west and south of the anchorage on the Golden Strand, I\'m sure of it!' WHERE `word_id` = 272162; -- locales_quest - details : entry=8886
UPDATE `trans_words` SET `content_loc4`='Thank goodness someone\'s finally taken me up on that offer. I was just thinking that I might need to triple the reward to get that stupid raptor killed, and our budget couldn\'t afford that!' WHERE `word_id` = 153972; -- locales_quest - offerrewardtext : entry=11184
UPDATE `trans_words` SET `content_loc4`='Thank goodness! Now I can get back to work. I know you think I\'m crazy, working on the piece that will earn me journeyman status away from the forge proper, but I can\'t run the risk of one of the other apprentices copying my design.' WHERE `word_id` = 150654; -- locales_quest - offerrewardtext : entry=10331
UPDATE `trans_words` SET `content_loc4`='Thank K\'aresh! He would have torn me apart!$B$BI\'m a Protectorate soldier, fleshling. My platoon was sent on a recon mission about 10 days ago. As we were moving across the upper ledges of this island, a group of Ethereum jumped us. At least I think it was Ethereum. They had the same energy color but their wrappings looked void.$B$BOnly Tyralius was spared. I think they put him in a prison cell.$B$BI\'ve got to get back to the Protectorate Watch Post! Can you escort me back?' WHERE `word_id` = 273139; -- locales_quest - details : entry=10425
UPDATE `trans_words` SET `content_loc4`='Thank the Earthmother! I parted ways with the others several days ago. I was intent on discovering the source of the disturbance that the spirits of water were feeling. My search led me to Coilskar where I discovered that the naga were befouling the water supply of Shadowmoon Valley.$B$BUnfortunately, I was discovered and taken prisoner. There\'s no telling what ghastly things they had in store for me.$B$BWe must leave this place! Help me escape and then seek out Torlok at the Altar of Damnation.' WHERE `word_id` = 273166; -- locales_quest - details : entry=10451
UPDATE `trans_words` SET `content_loc4`='Thank the Light! From the tone of the foreman\'s letter, it sounded as if that box of crystals contained the only known samples from the mine. If we had lost them, $N, we may never have seen another like them.$B$BI can\'t thank you enough for your help.' WHERE `word_id` = 146983; -- locales_quest - offerrewardtext : entry=9435
UPDATE `trans_words` SET `content_loc4`='Thank the spirits you\'re here! We must summon Ahune quickly before it\'s too late...' WHERE `word_id` = 274225; -- locales_quest - offerrewardtext : entry=11955
UPDATE `trans_words` SET `content_loc4`='Thank you $N! I am glad that Young Furbolg Shaman is fine.$B$BYou truly are blessed with the Light! Therefore, you shall use it to brign back the light in everyone!' WHERE `word_id` = 147640; -- locales_quest - offerrewardtext : entry=9599
UPDATE `trans_words` SET `content_loc4`='Thank you for bringing these seeds. I\'ve almost got enough to send back the first batch!' WHERE `word_id` = 149213; -- locales_quest - offerrewardtext : entry=9992
UPDATE `trans_words` SET `content_loc4`='Thank you for bringing this to me, $C. I was wondering why I hadn\'t heard from the Expedition in so long. I wonder how long ago this message was dispatched and what befell its carrier.$B$BHere\'s a bit of coin for your trouble.' WHERE `word_id` = 146771; -- locales_quest - offerrewardtext : entry=9373
UPDATE `trans_words` SET `content_loc4`='Thank you for bringing this venom, $N. It will go a long way toward making my demonstration possible without depleting my personal stores.' WHERE `word_id` = 148566; -- locales_quest - offerrewardtext : entry=9830
UPDATE `trans_words` SET `content_loc4`='Thank you for delivering this report, $C. From what Sentinel Luciel writes, it seems our worst fears have come to pass. With each advance we make, the Horde appears more determined to do as much damage as possible.$B$BWe can\'t afford to back down; the fate of our ancestral lands hangs in the balance. With more people like you on the frontlines, I\'m certain we could triumph. Will you join our fight?' WHERE `word_id` = 147320; -- locales_quest - offerrewardtext : entry=9521
UPDATE `trans_words` SET `content_loc4`='Thank you for helping Tavara. So many are unfamiliar with these lands and find trouble where they least expect.$B$BIt would honor me if you were to wear these robes. They mark you as one of the protectors of Azure Watch and a devout follower of the Light.' WHERE `word_id` = 147587; -- locales_quest - offerrewardtext : entry=9586
UPDATE `trans_words` SET `content_loc4`='Thank you for helping with this difficult task, $N. Our work in the Dead Mire is not yet done, but now our efforts will focus on doing what we can to restore balance to the area.' WHERE `word_id` = 148351; -- locales_quest - offerrewardtext : entry=9783
UPDATE `trans_words` SET `content_loc4`='Thank you for relieving my men, $N. With your help, and that of others, we may yet hold out. Unfortunately, merely holding out is not enough. We must go on the offensive.' WHERE `word_id` = 151480; -- locales_quest - offerrewardtext : entry=10562
UPDATE `trans_words` SET `content_loc4`='Thank you for rescuing one of our scouts, $N. We lose good people every day and there aren\'t enough new recruits to replace them.' WHERE `word_id` = 153615; -- locales_quest - offerrewardtext : entry=11085
UPDATE `trans_words` SET `content_loc4`='Thank you for returning this, $N. I\'m not sure what significance it holds for the wounded pilgrim, but she refuses to let anyone get near her until it\'s in her hands again.' WHERE `word_id` = 146786; -- locales_quest - offerrewardtext : entry=9376
UPDATE `trans_words` SET `content_loc4`='Thank you for saving my peons. Now maybe I can get them to fix my shredder with those parts you found. earn some money and get out of Outland. I can\'t wait to be back in Booty Bay.' WHERE `word_id` = 150221; -- locales_quest - offerrewardtext : entry=10238
UPDATE `trans_words` SET `content_loc4`='Thank you for this chance to once again see Salandria. I have missed her so much this past year, but we are still good friends.$B$BWe agreed that when we grow up, we\'re both going to be brave adventurers, just like you!' WHERE `word_id` = 273573; -- locales_quest - offerrewardtext : entry=10945
UPDATE `trans_words` SET `content_loc4`='Thank you for what you have done. Please continue to help us, $N. We have very few allies on which to call upon.' WHERE `word_id` = 145804; -- locales_quest - offerrewardtext : entry=6241
UPDATE `trans_words` SET `content_loc4`='Thank you for your help in clearing out some of the marshfang rippers, $N. If they had any intelligence, perhaps they\'d learn that it\'s best to live and let live.$B$B<Noraani shrugs.>$B$BGiant, overgrown bugs with intelligence. I wonder what that would be like...' WHERE `word_id` = 148391; -- locales_quest - offerrewardtext : entry=9791
UPDATE `trans_words` SET `content_loc4`='Thank you for your help, $N. Got anything up your sleeve to make the peons earn their keep?' WHERE `word_id` = 146685; -- locales_quest - offerrewardtext : entry=9342
UPDATE `trans_words` SET `content_loc4`='Thank you for your help, $N. No contribution to the war effort is too small. Our men are going to need every advantage they can get in this battle.' WHERE `word_id` = 154668; -- locales_quest - offerrewardtext : entry=11875
UPDATE `trans_words` SET `content_loc4`='Thank you for your help, $N. Your generosity will not be forgotten. As a token of my gratitude, I\'ve prepared a hot drink for you.' WHERE `word_id` = 148336; -- locales_quest - offerrewardtext : entry=9780
UPDATE `trans_words` SET `content_loc4`='Thank you so much, $N. His mother will deal with him from now on.' WHERE `word_id` = 148963; -- locales_quest - offerrewardtext : entry=9924
UPDATE `trans_words` SET `content_loc4`='Thank you! This will help keep our guys going out in the field until Moxie returns with the rest of the supplies from the zeppelin crash.' WHERE `word_id` = 154075; -- locales_quest - offerrewardtext : entry=11217
UPDATE `trans_words` SET `content_loc4`='Thank you, $C!  The recovery of this holy symbol means a great deal to myself and others that honor the memory of Uther Lightbringer.' WHERE `word_id` = 147155; -- locales_quest - offerrewardtext : entry=9474
UPDATE `trans_words` SET `content_loc4`='Thank you, $C. These should be enough so that I can examine this corruption. Then we will have a better idea of how to proceed.' WHERE `word_id` = 147248; -- locales_quest - offerrewardtext : entry=9503
UPDATE `trans_words` SET `content_loc4`='Thank you, $N. Among the chaos of battle, heroes burn bright against the darkness. Your light shines true!' WHERE `word_id` = 150322; -- locales_quest - offerrewardtext : entry=10259
UPDATE `trans_words` SET `content_loc4`='Thank you, $N. As Sporeggar\'s elder, I must ensure my people have adequate supplies. You make my task that much easier.' WHERE `word_id` = 148474; -- locales_quest - offerrewardtext : entry=9809
UPDATE `trans_words` SET `content_loc4`='Thank you, $N. Caring for those in need is a difficult task, but in time I\'ve discovered that it is its own reward.' WHERE `word_id` = 149335; -- locales_quest - offerrewardtext : entry=10020
UPDATE `trans_words` SET `content_loc4`='Thank you, $N. Corki\'s been sent away to live among the night elves in Blade\'s Edge. Hopefully they\'ll be able to keep him out of trouble.$B$BAbout that reward...' WHERE `word_id` = 149086; -- locales_quest - offerrewardtext : entry=9955
UPDATE `trans_words` SET `content_loc4`='Thank you, $N. Every little bit helps.$B$BI\'ve still got a long way to go, but let\'s face it, what else am I going to do between patrols?' WHERE `word_id` = 148309; -- locales_quest - offerrewardtext : entry=9774
UPDATE `trans_words` SET `content_loc4`='Thank you, $N. I will finally sleep comfortably at night. Were I not so old and weakened I would deal with my own enemies.$B$BTake this gold. It is not much, but your kindness cannot go unpaid.' WHERE `word_id` = 148216; -- locales_quest - offerrewardtext : entry=9747
UPDATE `trans_words` SET `content_loc4`='Thank you, $N. I\'ve been expecting this letter.' WHERE `word_id` = 146190; -- locales_quest - offerrewardtext : entry=8895
UPDATE `trans_words` SET `content_loc4`='Thank you, $N. My heart is saddened to see these talismans -- they underline the great losses we of Honor Hold have suffered against the fel orcs. But I am heartened to see you successful against them. If we can kill enough, perhaps we might emerge from this war victorious.' WHERE `word_id` = 151215; -- locales_quest - offerrewardtext : entry=10484
UPDATE `trans_words` SET `content_loc4`='Thank you, $N. Now that we have the wraiths under control we can send someone in to assess the damage. Hopefully this wasn\'t anything permanent.' WHERE `word_id` = 146053; -- locales_quest - offerrewardtext : entry=8486
UPDATE `trans_words` SET `content_loc4`='Thank you, $N. Perhaps with the help of the draenei, this marsh will one day be free of naga. I can\'t imagine what it must be like to be forced to serve them.$B$BHe doesn\'t talk about it often, but Maktu was once a slave.' WHERE `word_id` = 148866; -- locales_quest - offerrewardtext : entry=9902
UPDATE `trans_words` SET `content_loc4`='Thank you, $N. Their spirits will be at peace now.' WHERE `word_id` = 147350; -- locales_quest - offerrewardtext : entry=9527
UPDATE `trans_words` SET `content_loc4`='Thank you, $N. Tyralius suffered massive injuries but we think he\'s going to make it.$B$BHe mentioned fields of prisons, much like the one you rescued him from. Prisons full of creatures from all across the universe!$B$BWe\'ll be in touch should a need arise to crack those open. The Consortium has already expressed some interest in sending some teams out and is working on some technology to break through the Ethereum\'s locks.' WHERE `word_id` = 150978; -- locales_quest - offerrewardtext : entry=10422
UPDATE `trans_words` SET `content_loc4`='Thank you, $N. We will use these sooner than you might think.' WHERE `word_id` = 151728; -- locales_quest - offerrewardtext : entry=10618
UPDATE `trans_words` SET `content_loc4`='Thank you, $N.$B$B<Haalrun smiles sympathetically.>$B$BAt least you don\'t have to clean them. That\'s the worst job of the lot, but once it\'s done, you\'d never know they were attached to a giant insect.' WHERE `word_id` = 148387; -- locales_quest - offerrewardtext : entry=9790
UPDATE `trans_words` SET `content_loc4`='Thank you, $N.$B$BWhile we\'ll see to it that the wildlife will replenish itself in quick order, regrowing the trees, especially in a natural fashion, is a much more time-intensive process.' WHERE `word_id` = 152077; -- locales_quest - offerrewardtext : entry=10713
UPDATE `trans_words` SET `content_loc4`='Thank you, I do appreciate the prompt delivery.$B$BWhile you\'re here, please make yourself comfortable. If you haven\'t done so already, feel free to get a hearthstone and bind yourself here. Using a hearthstone will allow you to recall to an inn once every hour. If you ever lose your hearthstone, simply return to any inn - an innkeeper will gladly get you a new one.' WHERE `word_id` = 145977; -- locales_quest - offerrewardtext : entry=8350
UPDATE `trans_words` SET `content_loc4`='Thank you, my friend. Now I will be able to fashion more of the special alloy that is going into the creation of our forces\' weapons and armor.$B$BAnd, I have some leftover extract from the process, which you\'ll likely find handy for use on your weapons. Please, take your pick of whichever will serve your purposes.$B$BReturn to me again on the morrow. No doubt I will need more armaments.' WHERE `word_id` = 154556; -- locales_quest - offerrewardtext : entry=11544
UPDATE `trans_words` SET `content_loc4`='Thanks a lot for your help! As long as Brewfest is going and the Dark Iron Dwarves are attacking, I\'ll need some help shipping kegs. So if you want to work for more tickets, talk to me every day.$B$BBut enough about that, I need to pay you for the job you just did. Here are your tickets, have fun at Brewfest!' WHERE `word_id` = 153720; -- locales_quest - offerrewardtext : entry=11122
UPDATE `trans_words` SET `content_loc4`='Thanks for coming, $N. Things are worse than we had anticipated. The Wretched have taken over Sunsail Anchorage in its entirety.' WHERE `word_id` = 146540; -- locales_quest - offerrewardtext : entry=9256
UPDATE `trans_words` SET `content_loc4`='Thanks for giving me a hand, $N. I was dreading the thought of venturing into the woods this close to Deatholme.' WHERE `word_id` = 146596; -- locales_quest - offerrewardtext : entry=9281
UPDATE `trans_words` SET `content_loc4`='Thanks for taking care of the ray, $N. Goodness knows these guys fly much better when they\'re well-fed. We don\'t want our boys getting shot down because their ray was too weak to fly.' WHERE `word_id` = 153642; -- locales_quest - offerrewardtext : entry=11093
UPDATE `trans_words` SET `content_loc4`='Thanks for the supplies; we\'ll put them to good use!  A new recruit?  Well, let\'s see what you\'re made of.' WHERE `word_id` = 153666; -- locales_quest - offerrewardtext : entry=11098
UPDATE `trans_words` SET `content_loc4`='Thanks for your help! As long as Brewfest is going and the Dark Iron Dwarves are attacking and our supplier is stuck in that canyon, I\'ll need some help shipping kegs. So if you want to work for more tickets, talk to me every day.$B$BHere are the tickets we promised you. Have fun!' WHERE `word_id` = 154329; -- locales_quest - offerrewardtext : entry=11412
UPDATE `trans_words` SET `content_loc4`='Thanks for your help. You don\'t know what it\'s like to have to deal with "Doctor" Wafflefry over there. In between his "checkups" on my progress, he\'s busy hawking some gadget or another derived from his military inventions.' WHERE `word_id` = 154677; -- locales_quest - offerrewardtext : entry=11880
UPDATE `trans_words` SET `content_loc4`='Thanks to you, $N, I\'ll breathe a little easier. Figure of speech, of course!$B$BI suppose you\'ll want some form of reward now.' WHERE `word_id` = 152642; -- locales_quest - offerrewardtext : entry=10857
UPDATE `trans_words` SET `content_loc4`='Thanks to you, their plans are in ruin! Now only one thing remains... Salhadaar.' WHERE `word_id` = 150907; -- locales_quest - offerrewardtext : entry=10406
UPDATE `trans_words` SET `content_loc4`='Thanks! I can\'t remember the last time I had eel!$B$BHow should I cook \'em?' WHERE `word_id` = 149407; -- locales_quest - offerrewardtext : entry=10037
UPDATE `trans_words` SET `content_loc4`='Thanks, I\'ll hand these out to the lads.' WHERE `word_id` = 272120; -- locales_quest - offerrewardtext : entry=8780
UPDATE `trans_words` SET `content_loc4`='That blood elf witch, Cersei Dusksinger, has been cursing the Lost Ones, I just know it!  I don\'t know why or how, but the results are tragic. Several of the Lost Ones have now been further corrupted - cursed with even more fel energies!$B$B$C, can you find it in you to take pity on these poor creatures?  They should not have to live out the rest of their lives in such a horrible condition.$B$BYou\'ll find these cursed Lost Ones at the Fallow Sanctuary, east down the path.' WHERE `word_id` = 147047; -- locales_quest - details : entry=9448
UPDATE `trans_words` SET `content_loc4`='That Cipher fragment will never reach Zuluhed. You\'re going to ambush Ruul the Darkener and his transport before it has a chance to make the delivery.$B$BThe Dragonmaw Fortress is directly east of here, across the Netherwing Fields. Somewhere between Dragonmaw Fortress and the Sanctum of the Stars is where you\'ll find Ruul. Strike hard and strike fast! And $N, from the sound of it, this Ruul is going to be tough. Keep your guard up.$B$BReturn the Cipher to me should you succeed.' WHERE `word_id` = 273268; -- locales_quest - details : entry=10578
UPDATE `trans_words` SET `content_loc4`='That didn\'t take as long as I thought it would. Are you sure that you dealt with as many as was necessary?$B$BVery well, there\'s something else that I want you to help us with. Something far more important.' WHERE `word_id` = 151224; -- locales_quest - offerrewardtext : entry=10486
UPDATE `trans_words` SET `content_loc4`='That is a fine batch of spiritual soup if I ever did smell one. Just a pinch of pepper and it\'s done.$B$BAs promised, here\'s your cut. And because you did such a fine job, I\'m going to throw in some of my extra stock.' WHERE `word_id` = 154256; -- locales_quest - offerrewardtext : entry=11381
UPDATE `trans_words` SET `content_loc4`='That is excellent news, $N! I was worried the highlord might question the veracity of our information, but I see now my concern was misplaced.$B$BYou are to be well rewarded for your efforts on behalf of Theramore and the Alliance.' WHERE `word_id` = 154089; -- locales_quest - offerrewardtext : entry=11223
UPDATE `trans_words` SET `content_loc4`='That is excellent news, $N. Now that we know how to deal with these, I can have hirelings sabotage any remaining reavers.' WHERE `word_id` = 152631; -- locales_quest - offerrewardtext : entry=10855
UPDATE `trans_words` SET `content_loc4`='That is excellent news, indeed, $N. With progress to report from Hellfire Citadel, perhaps we can afford to turn our attention to the Fel Horde presence other parts of the peninsula and Outland as a whole.' WHERE `word_id` = 147217; -- locales_quest - offerrewardtext : entry=9493
UPDATE `trans_words` SET `content_loc4`='That is great news, great news indeed $N. I could not have asked for a better outcome. Your service to our people is most greatly appreciated, choose one of the following heroic items.$B$BYour name will forever be remembered in our peoples heart.' WHERE `word_id` = 152486; -- locales_quest - offerrewardtext : entry=10821
UPDATE `trans_words` SET `content_loc4`='That is most excellent news, my friend!$B$BYou and Drijya have done us a great service. With that warp' WHERE `word_id` = 150556; -- locales_quest - offerrewardtext : entry=10310
UPDATE `trans_words` SET `content_loc4`='That is the biggest, ugliest fish I\'ve seen. I can\'t even imagine what you used for bait.$B$BJust set the beast on the ground, and I\'ll deal with it once I\'m sure it\'s dead.' WHERE `word_id` = 154626; -- locales_quest - offerrewardtext : entry=11669
UPDATE `trans_words` SET `content_loc4`='That is unfortunate news! But, of course, I made the right choice in sending you out there to help Neftis. The last thing that we need is the Shadow Council establishing a strong point right on our doorstep!$B$B$N, you\'ve earned yourself something nice. Go ahead, take your pick.' WHERE `word_id` = 149433; -- locales_quest - offerrewardtext : entry=10043
UPDATE `trans_words` SET `content_loc4`='That key, I recognize it. That is Kadghar\'s key... of course I haven\'t given it to him yet.' WHERE `word_id` = 148595; -- locales_quest - offerrewardtext : entry=9836
UPDATE `trans_words` SET `content_loc4`='That last experiment was not a complete loss -- I did save a small bit of the blood you brought me. Now boiled down, it should be free of whatever curses the Bleeding Hollow witches placed on their people\'s blood.$B$BI need more for study. Go gather bleeding hollow blood from the orcs in Zeth\'Gor. When you\'ve gathered the blood, bring it to their cursed cauldron and boil it. Bring me the boiled blood.' WHERE `word_id` = 151383; -- locales_quest - details : entry=10538
UPDATE `trans_words` SET `content_loc4`='That letter is disturbing, and not just because it comes from the Betrayer. We must know how they are creating these new fel orcs if we\'re to defeat them!  It\'s bad enough that we must fight the Burning Legion on our eastern flank, but now a new army of fel orcs to the west!$B$BYou must infiltrate this Blood Furnace inside Hellfire Citadel. Bring back samples of blood from the fel orcs that you find in there.' WHERE `word_id` = 147604; -- locales_quest - details : entry=9590
UPDATE `trans_words` SET `content_loc4`='That makes three manaforges you\'ve shut down, $N. Kael\'thas must be having a real fit right about now.' WHERE `word_id` = 150690; -- locales_quest - offerrewardtext : entry=10338
UPDATE `trans_words` SET `content_loc4`='That mask you hold in your hands is a Felsworn gas mask and by the appearance, it\'s not damaged. The trouble we can cause Forge Camp: Anger is boundless. Are you interested in helping us out, $N?' WHERE `word_id` = 152446; -- locales_quest - offerrewardtext : entry=10812
UPDATE `trans_words` SET `content_loc4`='That scoundrel, Thaelis, finally met his fate. Good job, $N. Things should be quiet with the Wretched for a while.' WHERE `word_id` = 145998; -- locales_quest - offerrewardtext : entry=8468
UPDATE `trans_words` SET `content_loc4`='That should buy us some time... hopefully it will be enough for us to obtain more information.' WHERE `word_id` = 152003; -- locales_quest - offerrewardtext : entry=10688
UPDATE `trans_words` SET `content_loc4`='That should do the trick. I wouldn\'t want to be in the hold when that thing goes off.' WHERE `word_id` = 151647; -- locales_quest - offerrewardtext : entry=10597
UPDATE `trans_words` SET `content_loc4`='That was fast. Are you sure that you dealt with as many as was necessary?$B$BExcellent, there\'s something else that you can help us with if you are willing?' WHERE `word_id` = 151118; -- locales_quest - offerrewardtext : entry=10455
UPDATE `trans_words` SET `content_loc4`='That will set them back weeks! Fantastic work, $N. The Legion never expected such heavy resistance.' WHERE `word_id` = 149767; -- locales_quest - offerrewardtext : entry=10129
UPDATE `trans_words` SET `content_loc4`='That will slow them down, there\'s no denying it! I think you just put me in an uncharacteristically good mood, $N!' WHERE `word_id` = 149839; -- locales_quest - offerrewardtext : entry=10146
UPDATE `trans_words` SET `content_loc4`='That will surely teach those that don\'t do as I say...$B$BBy the way... where is Doomcryer? Why have you reported this revolt to me and not she?' WHERE `word_id` = 152481; -- locales_quest - offerrewardtext : entry=10820
UPDATE `trans_words` SET `content_loc4`='That worked better than I expected! Chalk up another one for good old goblin ingenuity!$B$BThe only problem is that it\'s hopelessly covered in dust! It\'s going to take me forever to get this thing cleaned up and replaced in its crate.$B$BOf course, time is the one thing I have in ample supply.' WHERE `word_id` = 146973; -- locales_quest - offerrewardtext : entry=9433
UPDATE `trans_words` SET `content_loc4`='That you have chosen to accept my offer speaks well of you, $N. I wish you luck in the tasks I am about to set for you, but I\'m certain you\'ll prove yourself more than capable.$B$BI will present the tasks in order from easiest to most difficult and we may begin when you are prepared.' WHERE `word_id` = 148110; -- locales_quest - offerrewardtext : entry=9722
UPDATE `trans_words` SET `content_loc4`='That\'ll be enough for today, overseer. Come back tomorrow for another round of clean up.' WHERE `word_id` = 153576; -- locales_quest - offerrewardtext : entry=11077
UPDATE `trans_words` SET `content_loc4`='That\'ll show those holy fanatics to mind their own business.' WHERE `word_id` = 146758; -- locales_quest - offerrewardtext : entry=9370
UPDATE `trans_words` SET `content_loc4`='That\'ll teach them not to mess with us. Good work, $N.' WHERE `word_id` = 146180; -- locales_quest - offerrewardtext : entry=8892
UPDATE `trans_words` SET `content_loc4`='That\'ll teach them! And, of course, now for your reward.$B$BThe demons of Forge Camp: Wrath, to the north, have created a special transporter on their southern border. This experimental gate is being used to expand the forge camp, and we don\'t think we need to tell you how that will effect us if they expand too far.$B$BHowever, if we were you, we\'d take this darkrune over to Gahk, and ask him how you can help. Just take it easy on him, $N. Like you, he\'s new here.' WHERE `word_id` = 153362; -- locales_quest - offerrewardtext : entry=11026
UPDATE `trans_words` SET `content_loc4`='That\'ll teach those damned, filthy animals not to kill my boars! Well done, $N!' WHERE `word_id` = 151318; -- locales_quest - offerrewardtext : entry=10515
UPDATE `trans_words` SET `content_loc4`='That\'s a fair job of butchery you managed there, $C. We\'ll get the miners back to work now. I\'m sure they\'ll be disappointed that their break is over, but there\'s armor and weaponry to be made, and that requires ore to be mined.$B$BHere, take this as payment for doing your duty. Go get your armor and weapons repaired, and have a drink on me.' WHERE `word_id` = 146456; -- locales_quest - offerrewardtext : entry=9192
UPDATE `trans_words` SET `content_loc4`='That\'s a good start, but based on what you witnessed there in the courtyard, as well as intelligence that I\'ve received, we have a far more pressing matter at hand!' WHERE `word_id` = 149232; -- locales_quest - offerrewardtext : entry=9996
UPDATE `trans_words` SET `content_loc4`='That\'s a good start, but we\'re going to need more than that if we\'re to press a respectable amount of oil!' WHERE `word_id` = 149218; -- locales_quest - offerrewardtext : entry=9993
UPDATE `trans_words` SET `content_loc4`='That\'s a good tale... <cough>...of how you bagged all of those stags, $C. But I think that you can do better. I\'ve got something else in mind for you.' WHERE `word_id` = 148693; -- locales_quest - offerrewardtext : entry=9857
UPDATE `trans_words` SET `content_loc4`='That\'s a relief, $C! I can assure you that if you hadn\'t deactivated those scrying orbs we would be in a more tenuous position than the one we\'re already in! To think, surrounded by enemies, and one of them able to spy upon our every move!$B$BBut now we don\'t need to worry about that thanks to you! Here, take this, I want you to be rewarded for your efforts and I just happen to have a few things here that I no longer have any use for.$B$BThank you again, $N!' WHERE `word_id` = 146405; -- locales_quest - offerrewardtext : entry=9169
UPDATE `trans_words` SET `content_loc4`='That\'s better!  There\'s nothing quite like ridding our world of a demon. And they make for worthy opponents as well, unlike the weak races of the Alliance.' WHERE `word_id` = 168975; -- locales_quest - offerrewardtext : entry=9534
UPDATE `trans_words` SET `content_loc4`='That\'s dire news indeed -- I completely overestimated Tomathren\'s ability to lead. I\'ll see to it that a group is dispatched to safely bring him and Valanna back in.$B$BUnfortunately, we never heard from Ranger Salissa, so I fear the worst in her case.$B$BYou\'ve done the Farstriders a great service this day, $C. Please take one of these as a small token of our appreciation.' WHERE `word_id` = 146308; -- locales_quest - offerrewardtext : entry=9146
UPDATE `trans_words` SET `content_loc4`='That\'s dire news indeed! Well, it would seem I made the right choice in sending you out there to help Weeks. The last thing that we want is the Shadow Council establishing themselves right on our doorstep!$B$B$N, in recognition of your efforts at Grangol\'var Village, I hereby grant you the right to your pick of any of these fine rewards.' WHERE `word_id` = 149429; -- locales_quest - offerrewardtext : entry=10042
UPDATE `trans_words` SET `content_loc4`='That\'s enough for today. Slow and steady is the key here. Too much disruption and they\'d turn their attention directly upon us; something that we don\'t want.$B$BCome back again tomorrow and we\'ll work on it some more.' WHERE `word_id` = 154540; -- locales_quest - offerrewardtext : entry=11541
UPDATE `trans_words` SET `content_loc4`='That\'s excellent news. The way is still far from perfectly safe, but the messengers are clever and resourceful.' WHERE `word_id` = 148580; -- locales_quest - offerrewardtext : entry=9833
UPDATE `trans_words` SET `content_loc4`='That\'s good news! That brew is gonna come in handy with what T\'chali is gonna ask you to do next.' WHERE `word_id` = 151417; -- locales_quest - offerrewardtext : entry=10545
UPDATE `trans_words` SET `content_loc4`='That\'s more like it! Alright, now I think we\'re in business.$B$BJust let me dump this stuff into the thing' WHERE `word_id` = 150162; -- locales_quest - offerrewardtext : entry=10226
UPDATE `trans_words` SET `content_loc4`='That\'s the spirit!$B$BI\'m glad we finally got some reinforcements, but I\'m not so sure about their attitude toward the Horde. Where I come from, anyone with a weapon pointed at you is an enemy and needs to be dealt with like one!' WHERE `word_id` = 149547; -- locales_quest - offerrewardtext : entry=10078
UPDATE `trans_words` SET `content_loc4`='The "dwarf," Glodrak Huntsniper, has asked you to capture 5 Stunned Wolpertingers using the Wolpertinger Net. He mentioned that you must be intoxicated to see them.' WHERE `word_id` = 154340; -- locales_quest - objectives : entry=11431
UPDATE `trans_words` SET `content_loc4`='The Aldor marksmen appear distracted with their drill, allowing you ample time to place the contents of the vial into the stew.' WHERE `word_id` = 149917; -- locales_quest - offerrewardtext : entry=10169
UPDATE `trans_words` SET `content_loc4`='The Alliance has recently moved in to the west, using their elven magics to grow a town and grove in a short matter of time. We must keep their defenses weak so that they do not also grow a spine.$B$BI have a plan that will do that and more. I want you to traverse the bridge to the southwest, which crosses over Bloodmaul Ravine and leads into the Living Grove.$B$BAttack their fey drake defenses in the Living Grove and bring back the magical dust that sometimes can be found upon their wings.' WHERE `word_id` = 151227; -- locales_quest - details : entry=10487
UPDATE `trans_words` SET `content_loc4`='The Alliance is an insidious enemy, $N. They claim to defend the Ashenvale forests to justify their acts of aggression against our territories in the Barrens.$B$BToday we have an opportunity to drive the enemy from our lands once and for all. Go to Warsong Gulch and take back what is rightfully ours!' WHERE `word_id` = 154149; -- locales_quest - details : entry=11342
UPDATE `trans_words` SET `content_loc4`='The ambassador sent word ahead, so I\'ve been expecting you.$B$BThe ambassador simply wants you to do as much as you can to help out around here. She said something about making sure to cement our relationship with the rest of the Horde.$B$BAsk around - there are others in need of help further back in the Post. And I hear that there\'s another Horde outpost called Zoram\'gar Outpost on the coast all the way to the west.' WHERE `word_id` = 146951; -- locales_quest - offerrewardtext : entry=9428
UPDATE `trans_words` SET `content_loc4`='The Ancient Shadowmoon Spirit has used you as a pawn! Now that you have collected the items he required, he has revealed himself to be Teron Gorefiend. To break free of his possession, you must do his bidding and slay Karsius the Ancient Watcher.$B$BIf you succeed, return to Chief Apothecary Hildagard at Shadowmoon Village.' WHERE `word_id` = 151814; -- locales_quest - objectives : entry=10639
UPDATE `trans_words` SET `content_loc4`='The Ango\'rosh threat is contained for the moment, but I\'m certain they won\'t give up that easily. I\'ve forgotten many things since the end of my life as a draenei, but the stubbornness of the ogres is an enduring memory.' WHERE `word_id` = 148590; -- locales_quest - offerrewardtext : entry=9835
UPDATE `trans_words` SET `content_loc4`='The arakkoa are smart creatures... smart as any gnome you ever met. They have this way with magic and a violent temper, however. $B$BLately they\'ve taken to attacking Honor Hold patrols, which is why we were sent here. See, Mirren and I are world-famous marksmen, we are!  But fowl and game are what we specialize in... not these... lightning-invoking birdmen.$B$BSo what do you say you give us a hand in this one assignment?  You\'ll find the Den of Haal\'esh to the southwest.' WHERE `word_id` = 146909; -- locales_quest - details : entry=9417
UPDATE `trans_words` SET `content_loc4`='The arakkoa have attacked us again, this time striking in the night and then using their dark magic to escape before we could charge out to meet them in numbers. They use their summoning circles to rescue their warriors from certain death but they cannot run from us if we attack their homes. Veil Reskk and Veil Shienor lie to the north and northeast. $B$BFind these arakkoa nests and kill as many as you can.' WHERE `word_id` = 152696; -- locales_quest - details : entry=10868
UPDATE `trans_words` SET `content_loc4`='The arakkoa seek to bring Terokk back beyond the veil of time and Rilak... wants to find a way to accelerate the process? Is he out of his mind?$B$B<Adaris lays back down as his strength fades.>$B$BYou\'ve done your duty, $N. It is time you let my men take over from here. This has become a purely military matter.' WHERE `word_id` = 153371; -- locales_quest - offerrewardtext : entry=11028
UPDATE `trans_words` SET `content_loc4`='The arakkoa tracks, accompanied by a pair of shallow ruts, lead north across the road. It seems the arakkoa dragged something away from the site of the battle, perhaps toward a nearby settlement.$B$BWhile the broken cart shows signs of a struggle, there\'s little information about Deirom\'s fate. Follow the arakkoa tracks north and continue your search for the merchant. If he was captured alive, there may still be a chance to rescue him.' WHERE `word_id` = 149033; -- locales_quest - details : entry=9941
UPDATE `trans_words` SET `content_loc4`='The arakkoa tracks, accompanied by a pair of shallow ruts, lead north across the road. It seems the arakkoa dragged something away from the site of the battle, perhaps toward a nearby settlement.$B$BWhile the broken cart shows signs of a struggle, there\'s little information about Dugar\'s fate. Follow the arakkoa tracks north and continue your search for the merchant. If he was captured alive, there may still be a chance to rescue him.' WHERE `word_id` = 149035; -- locales_quest - details : entry=9942
UPDATE `trans_words` SET `content_loc4`='The arcane fragments you collected will do nicely.$B$BNicely for what, you ask?  For making a splendid robe fit for a $C like yourself!$B$BAll we need now is some linen cloth and a spindleweb silk gland. You can find the silk gland from any spindleweb spider, preferably after killing them.' WHERE `word_id` = 147190; -- locales_quest - details : entry=9488
UPDATE `trans_words` SET `content_loc4`='The area\'s looking clearer already. The domes are quite useful, but the major drawback is the rampant growth they encourage in some places, accelerating the growth and proliferation of native species.' WHERE `word_id` = 150963; -- locales_quest - offerrewardtext : entry=10418
UPDATE `trans_words` SET `content_loc4`='The arms you captured in Blackhoof Village have not seen any combat beyond mere training exercises, which means the Grimtotems who destroyed the Shady Rest are still at large.$B$BTo the south, in the heart of the Quagmire, lives the witch Tabetha. Though she is a human, she has dealt honorably with us and trained some of our mages.$B$BAsk her for assistance in tracking down the band of Grimtotem responsible for burning the inn. The Alliance must not be allowed to think we were complicit in the attack.' WHERE `word_id` = 154022; -- locales_quest - details : entry=11203
UPDATE `trans_words` SET `content_loc4`='The Ashtongue Seer has been long dead. As you turn over his body, you spot writing scrawled on the floor.' WHERE `word_id` = 152038; -- locales_quest - offerrewardtext : entry=10705
UPDATE `trans_words` SET `content_loc4`='The Baa\'ri tablets! Yes, this might provide us with very useful information.' WHERE `word_id` = 151516; -- locales_quest - offerrewardtext : entry=10568
UPDATE `trans_words` SET `content_loc4`='The back of the Draenethyst Mine? The Bloodmaul clan must have stolen it from the Bladespire ogres.$B$BBah! That is too far away... too deep into Bloodmaul territory for me to travel.$B$BBut you. You know the path. You will go there in my stead!' WHERE `word_id` = 151361; -- locales_quest - offerrewardtext : entry=10525
UPDATE `trans_words` SET `content_loc4`='The bad news you bring fills my heart with sorrow. Two of our deadliest foes join forces and one of the dearest sons of Shattrath has lost his way.' WHERE `word_id` = 150806; -- locales_quest - offerrewardtext : entry=10381
UPDATE `trans_words` SET `content_loc4`='The balance of the elements has been disrupted by the violent intrusion of the draenei to the Vale. Your crash has caused the spirits of earth to be enraged and turn upon the other elements who have come to quell them. You will learn that an understanding of the elements can only come when all are in harmony.$B$BThus, it falls to you to seek a balance. Behind me in the grove you will find the restless spirits of earth warring upon the other three elements. Help restore the balance.' WHERE `word_id` = 147054; -- locales_quest - details : entry=9450
UPDATE `trans_words` SET `content_loc4`='The Banshee Queen has guided you well. This is precisely what I need to combine the fragments.$B$B<Mehlar watches as the vine interweaves through the fragments, binding them together.>$B$BThere!  Scourgebane, I shall call it. Be wary when you use it, for it serves you against its will.' WHERE `word_id` = 151630; -- locales_quest - offerrewardtext : entry=10593
UPDATE `trans_words` SET `content_loc4`='The battered corpse matches Zurai\'s description perfectly. Whatever killed the scout appears to have taken him by surprise, as his weapon is sheathed and the contents of a small inkwell stain the nearby ground.' WHERE `word_id` = 148295; -- locales_quest - offerrewardtext : entry=9771
UPDATE `trans_words` SET `content_loc4`='The battle against the Warsong Lumber Camp isn\'t being waged from Forest Song alone, $N. There\'s a more substantial Silverwing Sentinel presence at Silverwing Grove to the southwest, near the border with the Barrens. From the grove, Sentinel Farsong directs our efforts against those who would destroy our lands.$B$BI\'m a bit late in sending my latest report to Sentinel Farsong, but also hesitant to leave Forest Song unattended. If your travels take you near Silverwing Grove, would you deliver my report?' WHERE `word_id` = 147318; -- locales_quest - details : entry=9521
UPDATE `trans_words` SET `content_loc4`='The battle was hard and you fought well, $N. I am honored to have stood by your side in the battle against Goc. I am thankful beyond words.$B$BThe Mok\'Nathal should know of these great defeats, but I am not ready to face them yet. I know my father\'s mind, and I know my heart. There is still much I can learn and do to become stronger, to show to him that I am his true son.' WHERE `word_id` = 152157; -- locales_quest - offerrewardtext : entry=10742
UPDATE `trans_words` SET `content_loc4`='The best way to destroy the rest of the Legion\'s supplies is to use their own weapons against them. I\'ve been working on a rig that will let you take control of one of those Fel Reaver Sentinels below.$B$BThere are a couple things I need to work out, yet. For that, I\'ll need the manual that Doctor Maleficus keeps locked in a chest near the bottom of the ramp. He probably won\'t give up the key without a fight, so you\'ll need to capture it. Bring the fel reaver manual back to me and I\'ll do the rest.' WHERE `word_id` = 151700; -- locales_quest - details : entry=10611
UPDATE `trans_words` SET `content_loc4`='The Black Stalker is dead! Normally I would not revel in such butchery, but the pain that the great mother and her brood have put my people through over the centuries is unspeakable.$B$BIt is unfortunate that now another of her progeny will transform to replace her, but that will take time and give us some breathing room so that we will not have to worry about the marsh walkers for a while.' WHERE `word_id` = 148092; -- locales_quest - offerrewardtext : entry=9719
UPDATE `trans_words` SET `content_loc4`='The blood crystal radiates a sickly aura that seeps into the water of the cave.' WHERE `word_id` = 147501; -- locales_quest - offerrewardtext : entry=9565
UPDATE `trans_words` SET `content_loc4`='The blood knight Mehlar Dawnblade has requested an audience with you, $N. Mehlar can be found at the Bulwark, guarding the passage between Tirisfal Glades and the Western Plaguelands.$B$BHe is not known for his patience. It would not be wise to keep him waiting.' WHERE `word_id` = 147647; -- locales_quest - details : entry=9601
UPDATE `trans_words` SET `content_loc4`='The Blue Brothers Defeated' WHERE `word_id` = 149122; -- locales_quest - EndText : entry=9967
UPDATE `trans_words` SET `content_loc4`='The bodies of the $R survey team are scattered all over the ground here, pierced by naga weaponry. The area is littered with the remains of their supplies and survey equipment, but their data crystals are nowhere to be seen.' WHERE `word_id` = 147718; -- locales_quest - offerrewardtext : entry=9620
UPDATE `trans_words` SET `content_loc4`='The bonelashers of the Bone Wastes bit Mawg and made him sick. Now he wants all of them destroyed!$B$BWhile they can be found throughout the wastes, they are known to congregate at Carrion Hill in the north.$B$BReturn to Mawg Grimshot after you\'ve done the deed and he\'ll give you a good reward.' WHERE `word_id` = 149391; -- locales_quest - details : entry=10034
UPDATE `trans_words` SET `content_loc4`='The book is completely soaked. Instructor Antheol\'s not going to like this at all.$B$BHere, take this money. I have an idea.' WHERE `word_id` = 146221; -- locales_quest - offerrewardtext : entry=9062
UPDATE `trans_words` SET `content_loc4`='The Book of the Raven tells us the raven\'s followers captured and bound the spirits of his rivals within stone shrines. A guardian was set to watch over each, lest the raven\'s enemies attempt to free them.$B$BThe first of the shrines is the eagle\'s, found in the hills to the south of Lake Jorune and west of Stonebreaker Hold in Terokkar Forest.$B$BThe tablet says that the eagle fought valiantly, but was overcome by a swarm of enemies. The armor and endurance of your bear form is well-suited to this fight.' WHERE `word_id` = 153207; -- locales_quest - details : entry=10990
UPDATE `trans_words` SET `content_loc4`='The Boulderfist have moved the supplies they stole from our village to the twin clefts of Nagrand, found west of here in the Halaani Basin.$B$BWhile you are in the area, keep your eye out for the supply crates. If we can get those back, it would ease much of the burden that has fallen upon the matrons of Garadar.' WHERE `word_id` = 148923; -- locales_quest - details : entry=9916
UPDATE `trans_words` SET `content_loc4`='The Burning Legion has proven its capacity to corrupt mortal souls time and again. If I\'m to entrust you with the knowledge you seek, I must have proof of your allegiance.$B$BA demoness known as Sathal has eluded me for a long time as she\'s gifted with an incorporeal form.$B$BShe watches over the Wrath Priestesses as they do her bidding at Forge Base: Gehenna. Slay one of her minions and spill her blood on the ground. This affront should cause Sathal to materialize temporarily. Slay her while you can.' WHERE `word_id` = 151821; -- locales_quest - details : entry=10641
UPDATE `trans_words` SET `content_loc4`='The Burning Legion holds the Sun\'s Reach Armory, which is of tactical importance to our operation. As long as their morale remains high we will not wrest the building from their control.$B$BThe emissaries of hate are Legion shocktroopers sent to reinforce tactical Legion positions. The enemy rallies upon their arrival for they fear them more than they fear us.$B$BToday we will change that. Take this banner and slay the demons of the Legion. When the emissary is sent, slay and impale him.' WHERE `word_id` = 154526; -- locales_quest - details : entry=11538
UPDATE `trans_words` SET `content_loc4`='The Burning Legion is in cahoots with Kael? Hah! Voren\'thal is going to have a field day with this information.$B$BDo you think the thousands of pilgrims that have come from Azeroth want to be allied with the Burning Legion? I think not!' WHERE `word_id` = 150765; -- locales_quest - offerrewardtext : entry=10365
UPDATE `trans_words` SET `content_loc4`='The Burning Legion? Allied with Kael\'thas Sunstrider?$B$BThis is dire news. It explains much about what we\'ve seen here and in Shadowmoon.$B$BWe must send word back to Shattrath City immediately.' WHERE `word_id` = 150623; -- locales_quest - offerrewardtext : entry=10323
UPDATE `trans_words` SET `content_loc4`='The child is correct. I was once a "death priest."$B$B<Ramdor winces.>$B$BSuch an unsavory title.$B$BThrough that corridor lies the Auchenai Crypts. Inside you will find the remnants of my priory. They are all mad now, of course. Insane to the last! I suppose I stood out, being the last one among them with any common sense. Of course, I was excommunicated for my beliefs.$B$BWhat are my beliefs, you say?' WHERE `word_id` = 150167; -- locales_quest - offerrewardtext : entry=10227
UPDATE `trans_words` SET `content_loc4`='The children love to watch heroes at their work. It is my hope that, one day, each of these orphans will grow into a strong member of the Horde, and ultimately die in glorious battle!$B$BAh, thoughts of such a fine fate brings mist to my eyes...' WHERE `word_id` = 154161; -- locales_quest - offerrewardtext : entry=11357
UPDATE `trans_words` SET `content_loc4`='The Cipher of Damnation - History and Truth' WHERE `word_id` = 151341; -- locales_quest - EndText : entry=10519
UPDATE `trans_words` SET `content_loc4`='The cold of Ahune spreads through these caverns, $N; a seed of the frigid death that awaits us if he gains his full strength. I thank the Earthmother for sending you.' WHERE `word_id` = 274102; -- locales_quest - offerrewardtext : entry=11696
UPDATE `trans_words` SET `content_loc4`='The Consortium is well known for taking good care of its friends. At the beginning of each month I\'ll have some gems ready for you as payment for your services.$B$BThe quality of the gems you get will depend on how loyal you\'ve been to us. Here\'s to hoping you do the smart thing and stay with us.' WHERE `word_id` = 148800; -- locales_quest - offerrewardtext : entry=9884
UPDATE `trans_words` SET `content_loc4`='The corpse appears to be dressed in the manner of an Exodar technician, but the pendant described by Morae is absent.' WHERE `word_id` = 147553; -- locales_quest - offerrewardtext : entry=9578
UPDATE `trans_words` SET `content_loc4`='The corpse matches the description of Krun Spinebreaker, Hellfire Citadel\'s assassin. As you turn the body over, you notice a stone axe protruding from his back.' WHERE `word_id` = 146860; -- locales_quest - offerrewardtext : entry=9400
UPDATE `trans_words` SET `content_loc4`='The crawler was undoubtedly mean, but the timberstrider will test your resolve. More powerful allies require more tenacity from the hunter.$B$BTake this taming totem and befriend a greater timberstrider. They are found in the northern reaches of Azuremyst Isle.' WHERE `word_id` = 147613; -- locales_quest - details : entry=9592
UPDATE `trans_words` SET `content_loc4`='The cries of the Alliance as you and your compatriots attacked them could be heard even from here, $N. I can almost pity them for the pain and fear you delivered to their ranks. Almost, but not quite.$B$BYour value to the Defilers is without question. Your deeds in Arathi Basin have been most impressive, and have echoed among the chambers of Varimathras... and the Dark Lady herself.' WHERE `word_id` = 271901; -- locales_quest - offerrewardtext : entry=8122
UPDATE `trans_words` SET `content_loc4`='The crone has been slain! This is wonderful news, $N. Here is your reward.' WHERE `word_id` = 149013; -- locales_quest - offerrewardtext : entry=9935
UPDATE `trans_words` SET `content_loc4`='The crystal! Its power... now in my hands!' WHERE `word_id` = 152046; -- locales_quest - offerrewardtext : entry=10707
UPDATE `trans_words` SET `content_loc4`='The damp atmosphere of the marsh has taken a toll on my metal tools. It\'s made woodworking a lot more difficult than it ought to be, so I\'ve given up on metal blades.$B$BIt turns out that the marshfang slicers living to the south and west of town grow blades that are sharper than any steel I\'ve ever owned. They\'re also resistant to rust and wear. It\'s just a little tricky to attach them to a handle, but I think I\'ve got it figured out. I\'d appreciate any blades you could bring me.' WHERE `word_id` = 148620; -- locales_quest - details : entry=9842
UPDATE `trans_words` SET `content_loc4`='The Dark Portal Opened' WHERE `word_id` = 150499; -- locales_quest - EndText : entry=10297
UPDATE `trans_words` SET `content_loc4`='The decrease in native species is certainly a cause for concern. The increase in invasive species could be related to the recent disturbance in the ecosystem. I don\'t have enough information to make a conclusion, yet.$B$B<Lauranna jots down some notes on a journal as she goes through the plant parts.>$B$BTake these, $N. They might be of some use to you.$B$BBring me any other plant parts you find. I\'ll incorporate them into my statistics.' WHERE `word_id` = 148440; -- locales_quest - offerrewardtext : entry=9802
UPDATE `trans_words` SET `content_loc4`='The defeat of Naberius is a great achievement, $N. I can\'t help thinking that we might have had a chance against Kael\'thas\'s forces if the apprentice\'s loyalties had been in the right place that day.' WHERE `word_id` = 150607; -- locales_quest - offerrewardtext : entry=10320
UPDATE `trans_words` SET `content_loc4`='The demon dropped this parchment, which is amazingly written in the Common tongue!  The ones mentioned in it, Diathorus and Gorgannon, are the two infamous dreadlords that control the demons hereabouts.$B$BThe plans detail preparations for a demonic attack on Forest Song to the north; the location where a new draenei settlement is being established.$B$BPerhaps Vindicator Vedaar at Forest Song would be interested in these?' WHERE `word_id` = 147313; -- locales_quest - details : entry=9520
UPDATE `trans_words` SET `content_loc4`='The descendants held prisoner by the Skettis talonpriests are listed in this book. Gezzarak the Huntress, Garokk the Crystal Giant, the heretic known as Darkscreecher Akkarai and Vekkaz the Windrager. They will all need to be slain before Terokk returns to this world.$B$BYou will need the scrolls carried by the time-lost arakkoa and burn them at the summoning circles all over Skettis. Bring me a token from each one. These are powerful beings you will be facing; you would do well to bring friends.' WHERE `word_id` = 153560; -- locales_quest - details : entry=11074
UPDATE `trans_words` SET `content_loc4`='The descendants held prisoner by the Skettis talonpriests are listed in this book: Gezzarak the Huntress, Karrog the crystal giant, the heretic known as Darkscreecher Akkarai and Vakkiz the Windrager. They will all need to be slain before Terokk returns to this world.$B$BYou will need the scrolls carried by the time-lost arakkoa to call them forth at the summoning circles in Skettis. Bring me a token from each one. These are powerful beings you will be facing; you would do well to bring friends.' WHERE `word_id` = 153551; -- locales_quest - details : entry=11072
UPDATE `trans_words` SET `content_loc4`='The disturbances at the Expedition Armory continue, despite your best efforts. No one truly knows everything that happened there, nor exactly what binds the spirits of the fallen soldiers to the ruins.$B$BWe may be approaching this the wrong way. My visions suggested that each of the ghosts had a tie to the ruins, but what if their ties were to their leaders?$B$BArch Mage Xintor and Lieutenant Commander Thalvos were particularly close to their men, so perhaps overcoming their ghosts will calm the rest.' WHERE `word_id` = 149505; -- locales_quest - details : entry=10062
UPDATE `trans_words` SET `content_loc4`='The documents you found on Kargath\'s body detail plans on what appears to be a massive invasion on Honor Hold.' WHERE `word_id` = 147275; -- locales_quest - details : entry=9511
UPDATE `trans_words` SET `content_loc4`='The Dragonmaw only dare attack us when our guard is down. They would face certain defeat if they were to attack us while we were prepared.$B$BI want you to let them come, $N.$B$B<Ceyla nods.>$B$BYes... a trap. Take this flare that their overlord gave you and deliver it to Commander Arcus. He is standing guard right outside this building.' WHERE `word_id` = 273703; -- locales_quest - details : entry=11100
UPDATE `trans_words` SET `content_loc4`='The Dragonmaw only dare attack us when our guard is down. They would face certain defeat if they were to attack us while we were prepared.$B$BI want you to let them come, $N.$B$B<Thelis nods.>$B$BYes... a trap. Take this flare that their overlord gave you and deliver it to Commander Hobb. He is standing guard right outside this building.' WHERE `word_id` = 153649; -- locales_quest - details : entry=11095
UPDATE `trans_words` SET `content_loc4`='The dreadfangs are beaten. You have killed many and even their foul mother has fallen before you. I am too poisoned to fight my way to freedom.$B$BPlease, take some of my equipment. It will lighten my load and give you the tools you need to make the journey back to Sha\'tar Outpost.' WHERE `word_id` = 152928; -- locales_quest - offerrewardtext : entry=10921
UPDATE `trans_words` SET `content_loc4`='The dresses and pant suits you see are traditional clothes from many years past. They are worn in honor of old ways and loved ones gone.$B$BIf you bring me coins of ancestry, $N, then I will give you the pattern to create traditional red dresses or suits.' WHERE `word_id` = 272154; -- locales_quest - details : entry=8878
UPDATE `trans_words` SET `content_loc4`='The Earthen Ring thanks you, $N. The elements also give their thanks. Your continued efforts in cleansing our world are appreciated and noted.' WHERE `word_id` = 335164; -- locales_quest - offerrewardtext : entry=9815
UPDATE `trans_words` SET `content_loc4`='The elder springpaw is an efficient and swift hunter. In attempting to befriend one, it sees you as prey. Do not be dissuaded.$B$BUse the taming rod to bring the great cat to your side and hunt beside it. They are found throughout Eversong Woods, some very close to the lodge. When you have learned enough, return to me.' WHERE `word_id` = 147179; -- locales_quest - details : entry=9486
UPDATE `trans_words` SET `content_loc4`='The elements need your help, $R! Shadowmoon Valley has been subject to unspeakable evils. The spirits remain tortured to this day. Many are enraged and unwilling to commune with the Earthen Ring.$B$BHelp this world heal, friend.$B$BSoutheast of Shadowmoon Village - in the heart of Shadowmoon Valley - you will find an area known as the Altar of Damnation. Make your way there and speak with Earthmender Torlok.' WHERE `word_id` = 151987; -- locales_quest - details : entry=10681
UPDATE `trans_words` SET `content_loc4`='The elements sang of your victory the moment the disturbed essence was destroyed! The Earthen Ring thanks you and wishes for you to choose a reward.' WHERE `word_id` = 148479; -- locales_quest - offerrewardtext : entry=9810
UPDATE `trans_words` SET `content_loc4`='The energy contained in these samples is tremendous, $N. Now we just need to ensure that our access to this felblood continues uninterrupted.' WHERE `word_id` = 146749; -- locales_quest - offerrewardtext : entry=9366
UPDATE `trans_words` SET `content_loc4`='The essence of Aggonar lived on in the form of his foul offspring Aggonis. With your success in defeating the demon, we can begin to work towards restoring the pools to their former purity.$B$BYou have our thanks, $N. Your work will have a deep impact in this world.' WHERE `word_id` = 146945; -- locales_quest - offerrewardtext : entry=9427
UPDATE `trans_words` SET `content_loc4`='The Evergrove Druid has asked you to collect 5 Fel Cannonballs. Once you have done so, use the Druid Signal to call to the Evergrove Druid inside of Death\'s Door at the Blade\'s Edge Mountains.' WHERE `word_id` = 152850; -- locales_quest - objectives : entry=10904
UPDATE `trans_words` SET `content_loc4`='The Evergrove Druid has told you that the final thing you must do is to slay Baelmon the Hound-Master. Afterward, return to Evergrove in the Blade\'s Edge Mountains, and inform Wildlord Antelarion of your success.' WHERE `word_id` = 152884; -- locales_quest - objectives : entry=10912
UPDATE `trans_words` SET `content_loc4`='The evil is lifting from the Raven\'s Wood!  Can you feel it, $N?$B$BYou have saved us, and all of the surrounding wildlife, from certain destruction, little one!$B$BI keep stashed four magic rings amongst my boughs for such occasions as this, when a hero arises to do some great deed.$B$BYou are one such hero!  On behalf of all of the Raven\'s Wood, we give you thanks.' WHERE `word_id` = 152511; -- locales_quest - offerrewardtext : entry=10830
UPDATE `trans_words` SET `content_loc4`='The fel materials are removed from this world. Only the Light\'s residue remains.' WHERE `word_id` = 150974; -- locales_quest - offerrewardtext : entry=10421
UPDATE `trans_words` SET `content_loc4`='The fel reaver\'s engine begins to rumble and sputter as you supply it with the incorrect fuel.' WHERE `word_id` = 152604; -- locales_quest - offerrewardtext : entry=10850
UPDATE `trans_words` SET `content_loc4`='The fires are out! You and your fire brigade were victorious! Hah!$B$BThe Headless Horseman\'s attack has failed, but until he and his head are destroyed together, He will return.$B$BUntil then, however, let\'s take comfort in the safety you brought our children. I am honored to have watched your brave work today.' WHERE `word_id` = 154079; -- locales_quest - offerrewardtext : entry=11219
UPDATE `trans_words` SET `content_loc4`='The first step is complete. Hand me the Mark of the Lightbringer and I will transform it into something more suitable.$B$BThen my revenge will begin in earnest, $C!' WHERE `word_id` = 147026; -- locales_quest - offerrewardtext : entry=9443
UPDATE `trans_words` SET `content_loc4`='The fortress of Legion Hold is nigh impregnable, and to be completely honest, I can\'t spare the forces for an assault. Our hopes are riding on you, $N.$B$BWhat little information we\'ve been able to gather indicates that the Legion stocks and prepares its infernals at the hold. With the help of a disguise I\'ll give you, you must sneak into Legion Hold and learn about the their plans. Your best hope is to intercept or overhear communication between their commanders. Good luck, $N.' WHERE `word_id` = 151483; -- locales_quest - details : entry=10563
UPDATE `trans_words` SET `content_loc4`='The freed captives have already begun returning from Blackhoof Village. The information the captain is receiving from the prisoners will give us an advantage in our next battle against the Grimtotems.$B$BYou have my thanks and that of the men.' WHERE `word_id` = 153821; -- locales_quest - offerrewardtext : entry=11145
UPDATE `trans_words` SET `content_loc4`='The Great Fissure, though still dangerous, will prove to be a useful path for our pilgrims.$B$BWith the Alliance watching all the roads in and out of Falcon Watch this is the manner of desperate act that is required of us.' WHERE `word_id` = 146821; -- locales_quest - offerrewardtext : entry=9391
UPDATE `trans_words` SET `content_loc4`='The great spirit of air calls out to you, $N, and it is time for you to take heed.$B$BI wish for you to travel up the Wildwind Path on the north facing of the mountain that the Exodar has crashed into. At its peak you will find a cave and within will be one of my most accomplished pupils, Velaada.$B$BPlease, listen to her wisdom and do as she bids. When you return you will have a greater understanding of the element of air.' WHERE `word_id` = 147447; -- locales_quest - details : entry=9552
UPDATE `trans_words` SET `content_loc4`='The Grimscale murlocs have not been scared off by your efforts. But, I think I know what will send them packing, as well as get me some personal retribution.$B$BThey have a murloc chieftain that calls itself Mmmrrrggglll. It wanders the Golden Strand south of the river inlet. The beast once slew a compatriot of mine, and now wears his ring in open defiance and mockery.$B$BSlay Mmmrrrggglll, and bring back my friend\'s ring. Then we shall see these monsters cowed!' WHERE `word_id` = 146143; -- locales_quest - details : entry=8885
UPDATE `trans_words` SET `content_loc4`='The Grimtotems should have no trouble understanding our latest message.$B$BAs for you, your contact at Brackenwall Village will want to know of your success. I know the destruction of the Shady Rest Inn could be a source of tensions between the Horde and Alliance. If I encounter any investigators from Theramore, I will gladly tell them the Grimtotems acted on their own.' WHERE `word_id` = 154029; -- locales_quest - offerrewardtext : entry=11205
UPDATE `trans_words` SET `content_loc4`='The gronn we seek is Gorgrom the Dragon-Eater. With Sablemane\'s trap, you will make short work of the gronn. You will find him accepting sacrifices from the ogres of Boulder\'mok.$B$BFollow the road north from the Circle of Blood, then west at Ruuan Weald. Go northwest through the tunnel.$B$BFirst, kill the ogres for their heads. We will send a message. Set the trap at his altar. When he is dead, plant the grisly totems near him - none can mistake that message.$B$BTake the bear.$B$B<Misha growls.>' WHERE `word_id` = 152125; -- locales_quest - details : entry=10723
UPDATE `trans_words` SET `content_loc4`='The guardians of Skettis grow restless from their perch. Shattrath is in trouble. We will surely feel the brunt of Terokk\'s revenge.$B$BMuster a defense party quickly! Speak with Defender Grashna and let him know you are ready to bear arms against our attackers!' WHERE `word_id` = 152789; -- locales_quest - offerrewardtext : entry=10889
UPDATE `trans_words` SET `content_loc4`='The Headless Horseman is driven away, but he will return as long as Hallow\'s End looms. $N, end our misery, and the insane lament of the Horseman. Face him when he is joined with his head, and destroy it!$B$BHe rests in the Forlorn Cloister of the Scarlet Monastery, in Tirisfal Glade. Find his pumpkin shrine and say this:$B$B  Horseman, rise$B  Your time is nigh$B  You felt death once$B  Now, know demise!$B$BGo, $N. And may the Light go with you.' WHERE `word_id` = 153775; -- locales_quest - details : entry=11135
UPDATE `trans_words` SET `content_loc4`='The Horde is in your debt, $N. You\'ve made your people proud with your courage in defending our lands.' WHERE `word_id` = 155998; -- locales_quest - offerrewardtext : entry=11342
UPDATE `trans_words` SET `content_loc4`='The Image of Commander Ameer at Bash\'ir\'s Landing in the Blade\'s Edge Mountains wants you to use the Mana-Tombs Stasis Chamber Key on the Stasis Chamber inside the Mana-Tombs of Auchindoun.$B$BThis quest may only be completed on Heroic difficulty.' WHERE `word_id` = 153156; -- locales_quest - objectives : entry=10977
UPDATE `trans_words` SET `content_loc4`='The images on this totem depict a war between the Stillpine and the Bristlelimb furbolg tribes. The etchings show that the Bristlelimb have done unconscionable things to the furbolgs of Stillpine.$B$BAmidst the chaos, a prophecy has begun to unfold: A hero will rise - not of furbolg blood - and save the Stillpine tribe.$B$BSlay Bristlelimb furbolgs at Bristlelimb Village for their keys. Use the keys to free the Stillpine captives. Return to Arugoo of the Stillpine when you have freed them all!' WHERE `word_id` = 272499; -- locales_quest - details : entry=9544
UPDATE `trans_words` SET `content_loc4`='The information you\'ve brought is of vital importance, $N. Now that we know of the trolls\' plans we can prepare for an attack.$B$BWe won\'t be caught with our guard down.' WHERE `word_id` = 146745; -- locales_quest - offerrewardtext : entry=9363
UPDATE `trans_words` SET `content_loc4`='The journal you speak of... I was sent here to look for it wasn\'t I?' WHERE `word_id` = 147804; -- locales_quest - offerrewardtext : entry=9639
UPDATE `trans_words` SET `content_loc4`='The journal\'s most recent entries detail Featherbeard\'s concern about a growing number of Witherbark troll raids on the Wildhammer gryphon aeries and a rash of egg thefts. A search of the smaller Witherbark settlements in the area appears to have yielded no information.$B$BThe last entry describes Featherbeard\'s suspicion that a Witherbark witch doctor named Mai\'jin is behind the raids. The last, hastily written sentence states the dwarf\'s intention to travel to Shadra\'Alor in search of the eggs.' WHERE `word_id` = 147163; -- locales_quest - details : entry=9476
UPDATE `trans_words` SET `content_loc4`='The Kirin Tor hardly relish destruction for its own sake, but we have no qualms about acting against our enemies, even in death.' WHERE `word_id` = 150195; -- locales_quest - offerrewardtext : entry=10233
UPDATE `trans_words` SET `content_loc4`='The Legion will move against us as soon as their forces to the south are freed up. The Scourge at the Dead Scar are keeping their forces occupied at the moment. It won\'t last long, however.$B$BI want you to fly a dragonhawk over to the scar and put a dent in the demons\' numbers with these arcane charges.$B$BIf all goes well, they won\'t even notice us taking the armory from Kael\'s lackeys. Talk to Ayren, the dragonhawk master in the west part of the harbor, when you\'re ready to fly there.' WHERE `word_id` = 154502; -- locales_quest - details : entry=11532
UPDATE `trans_words` SET `content_loc4`='The lieutenant here rushed us into battle with the trolls across the lake instead of scouting the situation first. We were decimated by his stupidity! You hear me Tomathren? You\'re an idiot!$B$BWill you help us, $C? Those trolls still need to be dealt with. I need to stay here and nursemaid him -- I think he\'s lost his mind.$B$BJust head east to the other side of the lake into Zeb\'Sora. I want revenge... bring me their ears so that I can fashion a nice necklace!' WHERE `word_id` = 146293; -- locales_quest - details : entry=9143
UPDATE `trans_words` SET `content_loc4`='The Light be praised! You\'ve done what only a true heroine could have done, $N! The draenei you saved owes their continued life to your selflessness!$B$BPlease, take this supply pouch. You look like you can use it more than I.' WHERE `word_id` = 146605; -- locales_quest - offerrewardtext : entry=9283
UPDATE `trans_words` SET `content_loc4`='The Light might have forgotten me, but I have not forgotten the Light. These relics were once very sacred to my people.$B$BAllowing the Shadow Council to use their power for evil is... unthinkable!' WHERE `word_id` = 152757; -- locales_quest - offerrewardtext : entry=10881
UPDATE `trans_words` SET `content_loc4`='The Light smiles upon you, $N. We were all praying for your success.' WHERE `word_id` = 150507; -- locales_quest - offerrewardtext : entry=10299
UPDATE `trans_words` SET `content_loc4`='The Lightbringer appeared before you? That is amazing! And you made it all possible through your selfless help to the anchorite.$B$BIf even half of the stories are to be believed, Uther was a great man. And, like him, you are a true hero to all of the good people of Azeroth, $N!' WHERE `word_id` = 147037; -- locales_quest - offerrewardtext : entry=9446
UPDATE `trans_words` SET `content_loc4`='The magic practiced by the Lashh\'an arakkoa is unnatural. They rip out handfuls-clawfuls?-of their own feathers as reagents!$B$BThat doesn\'t sound like good magic to me. I\'ve come here to learn more about their spells. Unfortunately getting close is too much for me.$B$BYou could succeed: cast one of their spells on yourself, then return to me. You\'ll have to find one of their spellbooks, high up in the trees of Veil Lashh.$B$BAnd unless you\'ve grown feathers I can\'t see, you\'ll need to get some of those.' WHERE `word_id` = 273247; -- locales_quest - details : entry=10555
UPDATE `trans_words` SET `content_loc4`='The magister was concerned for my wellbeing? How sweet; I wish that he had shown such an interest before, when we were at the Spire.$B$BThat doesn\'t matter now. Please listen carefully, $C, as I have a favor to ask of you.' WHERE `word_id` = 146159; -- locales_quest - offerrewardtext : entry=8888
UPDATE `trans_words` SET `content_loc4`='The magma totem bursts into three separate flames as it is brought near the ice stone. The stone shimmers, then is gone...' WHERE `word_id` = 274099; -- locales_quest - offerrewardtext : entry=11691
UPDATE `trans_words` SET `content_loc4`='The Mark of the Nexus-King that you recovered has allowed me to forge a new key for the stasis chambers found inside the Mana-Tombs.$B$BAccording to the glyphs on the mark, two exist: one near the front and one near Nexus-Prince Shaffar. This key that I am about to give you will open the stasis chamber near the front.$B$BI want you to go to the Mana-Tombs of Auchindoun and open the Mana-Tombs Stasis Chamber.' WHERE `word_id` = 153155; -- locales_quest - details : entry=10977
UPDATE `trans_words` SET `content_loc4`='The memory is vivid, $N. My energies were wholly drained from the crash. I was only a dim speck of void energies when the Prophet Velen and his draenei compatriots lifted my remains and brought them here. After much ceremony, my diminished form was laid to rest within this sarcophagus. I have been regenerating for nearly a thousand years. The cycle is almost complete...' WHERE `word_id` = 149907; -- locales_quest - offerrewardtext : entry=10167
UPDATE `trans_words` SET `content_loc4`='The mine\'s been overrun by a plague of gan\'arg misfits, and on top of that, the peons have decided that this is a fine time for a nap! I don\'t know how they can sleep through all the disturbances coming from the mine, but that\'s the least of my problems.$B$BIf we don\'t get rid of those gan\'arg pests soon, Thrallmar\'s supply of ore and other minerals will be cut off. I\'ve already asked Watch Commander Krunk for help, but he says he can\'t spare the men with the town under constant attack. Will you help?' WHERE `word_id` = 149592; -- locales_quest - details : entry=10088
UPDATE `trans_words` SET `content_loc4`='The mine\'s been overrun by a plague of gan\'arg misfits, and on top of that, the peons have decided that this is a fine time for a nap! I don\'t know how they can sleep through all the disurbances coming from the mine, but that\'s the least of my problems.$B$BIf we don\'t get rid of those gan\'arg pests soon, Thrallmar\'s supply of ore and other minerals will be cut off. I\'ve already asked Watch Commander Krunk for help, but he says he can\'t spare the men with the town under constant attack. Will you help?' WHERE `word_id` = 150115; -- locales_quest - details : entry=10214
UPDATE `trans_words` SET `content_loc4`='The miners are working hard but none dare step foot further into the mines. They choose death rather than being forced to mine deeper! It is going to be your job to carry out the duties and tasks that they failed to complete.' WHERE `word_id` = 153567; -- locales_quest - offerrewardtext : entry=11075
UPDATE `trans_words` SET `content_loc4`='The naga are behind this!  But why?  From what you describe, that steam pump must be part of the cause for the water levels dropping in the marsh!$B$BNo matter - this explains their open hostility towards us. Let\'s not waste any time, I need you to perform another task for us.' WHERE `word_id` = 148076; -- locales_quest - offerrewardtext : entry=9716
UPDATE `trans_words` SET `content_loc4`='The naga have turned Serpent Lake into a giant water sink for the rest of Zangarmarsh. What could they possibly be doing?$B$BYou\'ve done well in bringing this news to us, $N. We will send a team to investigate this drain you\'ve discovered. I have a different task for you in the meanwhile.' WHERE `word_id` = 148152; -- locales_quest - offerrewardtext : entry=9731
UPDATE `trans_words` SET `content_loc4`='The news you bring about the naga activity in Zangarmarsh is dire. Ysiel was wise in sending word to us. She hasn\'t forgotten that the Expedition cannot exist without the Circle\'s support.' WHERE `word_id` = 148119; -- locales_quest - offerrewardtext : entry=9724
UPDATE `trans_words` SET `content_loc4`='The next two riders were the two top pilots that the dwarves and orcs of Azeroth had to offer. You stand no chance!' WHERE `word_id` = 153531; -- locales_quest - offerrewardtext : entry=11068
UPDATE `trans_words` SET `content_loc4`='The ogres of Bloodmaul and their master, Gorgrom, will trouble the Mok\'Nathal no longer.$B$BHowever, I worry about the effect our actions will have on the other gronn. I believe we must end this fight once and for all.' WHERE `word_id` = 152127; -- locales_quest - offerrewardtext : entry=10723
UPDATE `trans_words` SET `content_loc4`='The oil is finally ready for its first test. It seems heavy enough to serve as a high quality weapon oil.$B$BThe only way to find out is to have a professional try it out. See Grek over there by the wanted posters? Sure, he\'s no poet, but the orc knows his way around a weapon!$B$BTake a sample of the oil to him and ask him to use it on his weapon. After he\'s had a chance to test it out, let me know what he thinks of it.' WHERE `word_id` = 150054; -- locales_quest - details : entry=10201
UPDATE `trans_words` SET `content_loc4`='The oily seeds from the forest\'s olemba trees could prove very valuable. I\'d like to gather a larger quantity of them, press them for their oil, and then see what I can do with it.$B$BJust think, there\'s a whole forest full of trees out there, producing a lot of seeds that go to waste. I don\'t care if it turns out to be cooking oil, lamp oil, or weapon oil, I won\'t let it go to waste!$B$BBring me any of the seeds that you happen to gather from the olemba cones. They\'re all over the place.' WHERE `word_id` = 149216; -- locales_quest - details : entry=9993
UPDATE `trans_words` SET `content_loc4`='The orcish ruins of Zeth\'Gor to serve as a base for an ever-growing tide of orcs emerging from Hellfire Citadel.$B$BShadow Hunter Ty\'jin sent me here to keep an eye on the outpost and its strange inhabitants.$B$BSomething has changed these orcs, augmented them in a way we\'ve never seen before. We have to do anything we can to keep them from strengthening. Fight your way into Zeth\'Gor and destroy any supplies you find there.' WHERE `word_id` = 149865; -- locales_quest - details : entry=10158
UPDATE `trans_words` SET `content_loc4`='The orcish ruins of Zeth\'Gor to the southeast serve as a base for an ever-growing tide of orcs emerging from Hellfire Citadel.$B$BThe Sons of Lothar have been fighting them for some time, and the stories they tell would be nigh unbelievable if I hadn\'t seen these \'fel\' orcs with my own eyes.$B$BSomething has changed them, augmented them in a way we\'ve never seen before. We have to do anything we can to keep them from strengthening. Go to Zeth\'Gor and destroy any supplies you find there.' WHERE `word_id` = 149485; -- locales_quest - details : entry=10056
UPDATE `trans_words` SET `content_loc4`='The orders, signed by \'Warboss Nekrogg,\' order the fel orc patrols stationed at this camp to bring their human prisoner south for interrogation.$B$BLieutenant Amadi mentioned that the Southern Rampart is dotted with fel orc camps. Perhaps the next large section of the wall to the south is where they took Private Imarion.$B$BJudging by the note\'s mention of interrogation, it seems the private\'s fel orc captors see some value in keeping him alive. Pick up the trail before they decide to change their minds.' WHERE `word_id` = 149756; -- locales_quest - details : entry=10126
UPDATE `trans_words` SET `content_loc4`='The orders, signed by \'Warboss Nekrogg,\' order the fel orc patrols stationed at this camp to bring their orcish prisoner south for interrogation.$B$BShadow Hunter Ty\'jin mentioned that the Northern Rampart is dotted with fel orc camps. Perhaps the next large section of the wall to the south is where they took Scout Makha.$B$BJudging by the note\'s mention of interrogation, it seems the scout\'s fel orc captors see some value in keeping her alive. Pick up the trail before they decide to change their minds.' WHERE `word_id` = 149850; -- locales_quest - details : entry=10151
UPDATE `trans_words` SET `content_loc4`='The origin of this shrine is unknown, but there is no doubt to whom it pays homage...' WHERE `word_id` = 153777; -- locales_quest - offerrewardtext : entry=11135
UPDATE `trans_words` SET `content_loc4`='The orphan matron\'s stories don\'t even come close to how cool this place is. It\'s amazing!  She said that it was created by a powerful wizard named Medivh.$B$BWhat\'s on the other side of it?  You\'ve been there, right?  We should most definitely go someplace on the other side of the Dark Portal!' WHERE `word_id` = 273580; -- locales_quest - offerrewardtext : entry=10952
UPDATE `trans_words` SET `content_loc4`='The other windrider pilots speak highly of you, $N. You\'ve scored quite a lot of kills on the Abyssal Shelf. Keep up the good works!' WHERE `word_id` = 150731; -- locales_quest - offerrewardtext : entry=10347
UPDATE `trans_words` SET `content_loc4`='The outrunner\'s belongings have all been stolen with the exception of one package. It seems to match Alarion\'s description of Erona\'s package.' WHERE `word_id` = 148027; -- locales_quest - offerrewardtext : entry=9704
UPDATE `trans_words` SET `content_loc4`='The owlbeasts of Moonwing Den on Silvermyst Isle, far to the southwest, have been deluded into thinking that Hauteur is a god to be worshipped. What he truly is, is a fallen elemental who needs to be put out of his misery. That torch is the key to his downfall.$B$BHauteur regularly demands that these owlbeasts burn an effigy in his honor. So, when you light it, he will come. Slay him and bring me the ashes. Then use the orb in the satchel that I will give you to return to me here.' WHERE `word_id` = 147119; -- locales_quest - details : entry=9467
UPDATE `trans_words` SET `content_loc4`='The parchment holds a crudely painted map of Nagrand. There is a big red \'X\' where Telaar would be on the map. It would seem that these savages are planning an attack on the city. Take the Murkblood invasion plans to Arechron in Telaar.' WHERE `word_id` = 272754; -- locales_quest - details : entry=9871
UPDATE `trans_words` SET `content_loc4`='The patch of swamp to the northeast is home to a particularly nasty breed of raptors. We\'ve lost more than one unwary soldier to them, but I think we can turn them against our Grimtotem neighbors.$B$BTake this rope and use it to capture bloodfen raptors or bloodfen screechers that you\'ve subdued. I wouldn\'t try to capture one of the beasts until you\'ve weakened them quite a bit in combat. It\'s going to be quite a struggle.' WHERE `word_id` = 153825; -- locales_quest - details : entry=11146
UPDATE `trans_words` SET `content_loc4`='The Path of Conquest Discovered' WHERE `word_id` = 152185; -- locales_quest - EndText : entry=10750
UPDATE `trans_words` SET `content_loc4`='The path of the Light and purity is not an easy one to walk, $N. Even I, at times, struggle with following its course.$B$BYou should seek out my master, Jol, at the Exodar. She trained me when I was beginning my journey, and I believe she will find in you a new star pupil.$B$BYou will find Jol in the Vault of Lights of the Exodar at the Vindicators\' Sanctum, a room surrounded by a vibrant glow of light.' WHERE `word_id` = 150766; -- locales_quest - details : entry=10366
UPDATE `trans_words` SET `content_loc4`='The pelt is even more magnificent than I had hoped! This is a good omen, $N, and the spirits will favor you for your contribution to this ceremony.$B$BI have fashioned a helmet for you from the remaining pelts, $N. It identifies you as a mighty hunter and an ally of the great wolf spirit of Terokkar Forest.' WHERE `word_id` = 149349; -- locales_quest - offerrewardtext : entry=10023
UPDATE `trans_words` SET `content_loc4`='The plan is perfect, $N. I am already seeing the fruits of your labor.' WHERE `word_id` = 148991; -- locales_quest - offerrewardtext : entry=9931
UPDATE `trans_words` SET `content_loc4`='The plan succeeds or fails on how well these explosives are made...$B$BAs much as I\'d hate to rely on anything made by a fel orc, it\'s my only real chance to get out of here. Take this and throw it against the banner hanging from the tower up there.$B$BOnce it explodes, Warboss Nekrogg won\'t be able to resist the chance to make an example of the troublemaker. Kill him, take the key, and then open my cage. Good luck, $N.' WHERE `word_id` = 149859; -- locales_quest - details : entry=10155
UPDATE `trans_words` SET `content_loc4`='The plan succeeds or fails on how well these explosives are made...$B$BAs much as I\'d hate to rely on anything made by an orc, it\'s my only real chance to get out of here. Take this and throw it against the banner hanging from the tower up there.$B$BOnce it explodes, Warboss Nekrogg won\'t be able to resist the chance to make an example of the troublemaker. Kill him, take the key, and then open my cage. Good luck, $N.' WHERE `word_id` = 149799; -- locales_quest - details : entry=10137
UPDATE `trans_words` SET `content_loc4`='The potion that you drank has no real effect. You can only see a spirit if they allow it. The potion is merely a symbol of your belief and dedication. It was a test and you passed...' WHERE `word_id` = 149561; -- locales_quest - offerrewardtext : entry=10081
UPDATE `trans_words` SET `content_loc4`='The power contained in this tome is tremendous. Yet it is easily condensed into a single rune by one with a clear mind.' WHERE `word_id` = 150966; -- locales_quest - offerrewardtext : entry=10419
UPDATE `trans_words` SET `content_loc4`='The Promised One has emerged victorious!$B$BThe prophecy is nearly complete. Now about that hide...' WHERE `word_id` = 147521; -- locales_quest - offerrewardtext : entry=9570
UPDATE `trans_words` SET `content_loc4`='The prophecy spoke of one that would rise and deliver us from the oppressive grasp of Bristlelimb -- that the spirits themselves would herald the arrival of this hero.$B$BAnd now, you have come.$B$BI humble myself before you, great one.' WHERE `word_id` = 147421; -- locales_quest - offerrewardtext : entry=9544
UPDATE `trans_words` SET `content_loc4`='The Protectorate\'s reason for existence is now fully realized.$B$BThe Ethereum are not here to hunt Dimensius! They are here to become void!$B$BThey are manipulating the void energy at this manaforge to bond their own energy into a creature known as a nexus-stalker.$B$BThey are undoubtedly using a proto-accelerator to complete the transformation.' WHERE `word_id` = 150822; -- locales_quest - offerrewardtext : entry=10385
UPDATE `trans_words` SET `content_loc4`='The Razaani ethereals have strange devices that emit small, pink floating light orbs. They remind me of souls.$B$BThey are not to be confused with the large yellow balls that break free of the ethereal when they die, these are something else entirely.$B$BI\'d like for you to head north to Razaan\'s Landing and drop some totems, which will attact the pink light orbs.$B$BBring them back to me so that I might determine exactly what they are.' WHERE `word_id` = 152651; -- locales_quest - details : entry=10859
UPDATE `trans_words` SET `content_loc4`='The remains of the demonic Burning Legion have been making raids against our Warsong Lumber Camp!$B$BNo more!$B$BThe demons are attacking from the areas known as Felfire Hill, Demon Fall Canyon and Demon Fall Ridge to the east.$B$BGo and destroy as many as you can to bring glory to both yourself and the Horde!' WHERE `word_id` = 147380; -- locales_quest - details : entry=9534
UPDATE `trans_words` SET `content_loc4`='The rumors were true!  The stone of light and the stone of flame do exist!  We shall use their power to create weapons against Dar\'Khan.$B$BUse them when the time is right and the energies of the Sunwell, trapped within these objects, will vanquish him with the very power he sought to steal from us!' WHERE `word_id` = 146439; -- locales_quest - offerrewardtext : entry=9176
UPDATE `trans_words` SET `content_loc4`='The sands of time flow forwards easily. They can be made to flow backward, but with great difficulty.' WHERE `word_id` = 153679; -- locales_quest - offerrewardtext : entry=11103
UPDATE `trans_words` SET `content_loc4`='The Scourge has felt our wrath, $N. It won\'t be long before their cursed citadel lies in ruins.$B$BWith their numbers weakened, it is time to launch an attack on more strategic targets.' WHERE `word_id` = 146510; -- locales_quest - offerrewardtext : entry=9220
UPDATE `trans_words` SET `content_loc4`='The scourgestones are weak when separated from the skeletal grasp that once held them. Simply holding them in my hands has caused them to fracture and break.$B$B<Mehlar crushes the scourgestones into a pile of rubble.>$B$BTake these fragments to the Banshee Queen, Lady Sylvanas Windrunner, at her throne in Undercity. With her lies the knowledge to turn these remains into a weapon against the Scourge.' WHERE `word_id` = 151623; -- locales_quest - details : entry=10592
UPDATE `trans_words` SET `content_loc4`='The sentinels within the Arcatraz have gone haywire. The prisoners run rampant and the guards attack those that would help.$B$BMah\'duun hears the speaker on the nether winds. The speaker says to destroy the sentinels$B$BAccomplish this and today\'s bounty is yours.' WHERE `word_id` = 154294; -- locales_quest - details : entry=11389
UPDATE `trans_words` SET `content_loc4`='The Shattered Hand orcs are foul and beyond hope, but my heart is saddened that the road to salvation for Colonel Jules holds such bloodshed.' WHERE `word_id` = 152869; -- locales_quest - offerrewardtext : entry=10909
UPDATE `trans_words` SET `content_loc4`='The signet you now hold is as powerful as that of any Archmage of the Council. Wear it wherever you go so that the initiated may recognize your allegiance. Wear it wherever you go so its power may serve you as you\'ve served us!' WHERE `word_id` = 152139; -- locales_quest - offerrewardtext : entry=10725
UPDATE `trans_words` SET `content_loc4`='The signs were all there but I turned a blind eye.$B$BThe Legion\'s grasp grows stronger every day. Even in these parts...$B$BHad I known... Had I known that my boy was communing with demons... They offered him power and then gave him a taste of that power. That was all that it took...$B$BThe book is gone. Taken by Levixus... His plans are known only to me and his dark master.$B$BStranger, I don\'t know if you\'ve ever done right in your life, but know this: the boy must be stopped.' WHERE `word_id` = 150284; -- locales_quest - offerrewardtext : entry=10251
UPDATE `trans_words` SET `content_loc4`='The Sketh\'lon Wreckage, just to the east, is an eerie place. The peons are convinced that it\'s cursed.$B$BThere are few trees here, so the peons have no choice but to venture into the arakkoa ruins to chop wood. The arakkoa spirits prey on the work groups, draining their life and using it to fuel their foul magics.$B$BWe must find out what is driving the dark conclave and stop them before their influence expands. Search the Sketh\'lon Wreckage for evidence of their plans.' WHERE `word_id` = 152224; -- locales_quest - details : entry=10760
UPDATE `trans_words` SET `content_loc4`='The Sons appreciate your efforts, even if few are willing to speak of the armory.' WHERE `word_id` = 149463; -- locales_quest - offerrewardtext : entry=10050
UPDATE `trans_words` SET `content_loc4`='The source of Illidan Stormrage\'s fel orc army has been vanquished. The manufactory halts production of its grim product - at least for now. All that remains are a few trinkets and this severed head - a fitting trophy for a hero of the Alliance.$B$BReturn Magtheridon\'s head to Danath Trollbane at Honor Hold.' WHERE `word_id` = 273617; -- locales_quest - details : entry=11002
UPDATE `trans_words` SET `content_loc4`='The source of Illidan Stormrage\'s fel orc army has been vanquished. The manufactory halts production of its grim product - at least for now. All that remains are a few trinkets and this severed head - a fitting trophy for a hero of the Horde.$B$BReturn Magtheridon\'s head to Nazgrel at Thrallmar.' WHERE `word_id` = 273619; -- locales_quest - details : entry=11003
UPDATE `trans_words` SET `content_loc4`='The spirits are closer to finding peace and have bestowed their favor onto us.$B$BA humiliating blow to our enemy that their own ancestors would side against them, even if only temporarily.' WHERE `word_id` = 273980; -- locales_quest - offerrewardtext : entry=11506
UPDATE `trans_words` SET `content_loc4`='The spirits are one step closer to resting in peace. It is good to incur their favor, especially when waging war so close to their place of rest.' WHERE `word_id` = 273978; -- locales_quest - offerrewardtext : entry=11505
UPDATE `trans_words` SET `content_loc4`='The Steamwheedle Cartel\'s new outpost in the southern part of the marsh isn\'t faring so well. The zeppelin carrying supplies to Mudsprocket crashed, leaving them without vital tools, food, and medical supplies.$B$BFollow the road down south and into the marsh\'s interior. You\'ll pass by Tabetha\'s Farm and the Stonemaul Ruins before the road brings you to Mudsprocket. Offer any help you can provide to the town\'s leadership.' WHERE `word_id` = 154067; -- locales_quest - details : entry=11215
UPDATE `trans_words` SET `content_loc4`='The stories don\'t even come close to how cool this place is. It\'s amazing!$B$BIs it true that a powerful wizard named Medivh created it?$B$BI wonder what\'s on the other side?  You\'ve been there, right?  Hmm, we should definitely go someplace on the other side of the Dark Portal!' WHERE `word_id` = 273578; -- locales_quest - offerrewardtext : entry=10951
UPDATE `trans_words` SET `content_loc4`='The struggle to free the orcish people from our tainted blood has not been easy, $N. Warchief Thrall has seen too many orcish heroes laid low by fate\'s hand. Now, it seems Kargath Bladefist numbers among them.$B$BIn the Shattered Halls of Hellfire Citadel, Kargath has claimed the title of \'Warchief\' of the Fel Horde. With an army of crazed, bloodthirsty fel orcs at his back, he won\'t rest until Thrallmar is destroyed. Warchief Thrall has commanded us to defeat Kargath and his forces at all costs.' WHERE `word_id` = 147225; -- locales_quest - details : entry=9495
UPDATE `trans_words` SET `content_loc4`='The Sun Gate Destroyed' WHERE `word_id` = 148191; -- locales_quest - ObjectiveText1 : entry=9740
UPDATE `trans_words` SET `content_loc4`='The swamp gas is truly amazing, and can be very useful, mon!  But first, you gotta be able to suck it up, and that, my friend, requires a Zapthrottle Mote Extractor.$B$BNow, I could just give you the schematics, but I\'ll need something in return first. How about some spare parts?  Last time I checked, there were plenty laying around near the broken steam pump at the Dead Mire, far to the northeast.$B$BI heard that the withered giants spread the pieces all over that dried-out lake bed.' WHERE `word_id` = 147785; -- locales_quest - details : entry=9635
UPDATE `trans_words` SET `content_loc4`='The Tale of Neltharaku' WHERE `word_id` = 152459; -- locales_quest - EndText : entry=10814
UPDATE `trans_words` SET `content_loc4`='The tastes of my customers range from the mundane to the exotic. And sometimes they want to do very mundane things with the exotic objects they seek.$B$BSuch is the case with my latest request. My client, who shall remain nameless, wishes to obtain the head of the Epoch Hunter to mount on his wall.$B$B$C, will you find a way to travel back in time to Old Hillsbrad and procure said head for my trophy-hunting patron?' WHERE `word_id` = 154239; -- locales_quest - details : entry=11378
UPDATE `trans_words` SET `content_loc4`='The text on the note is written in some dark, indecipherable scrawl. The more you stare at them, the more the symbols seem to twist and burn before your eyes. There is only one at Honor Hold who can decipher such archaic script - the draenei mystic, Kryv. $B$BTake the Burning Legion Missive to Warp-Scryer Kryv who resides within the War-Room at Honor Hold.' WHERE `word_id` = 273109; -- locales_quest - details : entry=10395
UPDATE `trans_words` SET `content_loc4`='The text on the note is written in some dark, indecipherable scrawl. The more you stare at them, the more the symbols seem to twist and burn before your eyes. There is only one in Thrallmar that can transcribe such vile script - the blood elf Magister Bloodhawk.' WHERE `word_id` = 273105; -- locales_quest - details : entry=10393
UPDATE `trans_words` SET `content_loc4`='The Thunderlord were once a proud orc clan before they were transformed into fel orcs through the blood of Mannoroth.$B$BRecently we recovered this orc stronghold from the ogres that infest the ravine below. They had moved in when the Thunderlord clan abandoned it.$B$BWe will keep this place and I will have your help doing so.$B$BIn the Jagged Ridge to the south and the west, there are bloodletters that have become like a plague upon us. They eat and breed incessantly.$B$BKill them and then return to me.' WHERE `word_id` = 151222; -- locales_quest - details : entry=10486
UPDATE `trans_words` SET `content_loc4`='The Timbermaw tend to stay to themselves, $N. While we do allow safe passage to those that have proven they can be trusted, we try to avoid other furbolg tribes if at all possible.$B$BLately I have noticed that the Winterfall tribe has become increasingly hostile towards us. They seem to be in a state of rage, completely engulfed by their own fear and hatred of anything they do not understand.$B$BIf you could reduce their numbers this would help us greatly, $N.' WHERE `word_id` = 145802; -- locales_quest - details : entry=6241
UPDATE `trans_words` SET `content_loc4`='The time has come, $N, for you to learn about what it will take for you to acquire something you have no doubt waited on for some time... your charger.$B$BSpeak with Lord Grayson Shadowbreaker in Stormwind\'s Cathedral District. It is he who will guide you not only though the legerdemain of a Paladin\'s most trusted mount, but the tasks before you to acquire one of your own.$B$BI wish you the best in this trial. For the Light, noble $G brother : sister;!' WHERE `word_id` = 145870; -- locales_quest - details : entry=7670
UPDATE `trans_words` SET `content_loc4`='The time has come, $N. You have grown to the point where you are ready to commune directly with the elemental spirits of this world.$B$BBut be warned. I can sense that the manner of our entry into this world, and its unfortunate side effects, have angered the earth spirits and caused an imbalance of the elements.$B$BIf you are to create your earth totem, you must first speak with the Spirit of the Vale. You will find it in the Sacred Grove, nestled in the hills to the northwest.' WHERE `word_id` = 147051; -- locales_quest - details : entry=9449
UPDATE `trans_words` SET `content_loc4`='The time to strike has come! With the tear gone, Dimensius is vulnerable to physical and magical attacks!' WHERE `word_id` = 151057; -- locales_quest - offerrewardtext : entry=10438
UPDATE `trans_words` SET `content_loc4`='The Tomb of Lights has become corrupted by the Shadow Council and the draenei dead have become cursed spirits, angry and confused with a burning hatred for the living. $B$BBefore I left Shattrath I was given a draenei tomb relic. It can be used near the Ancient Draenei Altar deep within the Tomb of Lights to summon a tomb guardian. This creature will activate the altar and will begin to purge the tomb of corrupt spirits, freeing them to ascend. Protect the tomb guardian until the ritual is complete.' WHERE `word_id` = 152568; -- locales_quest - details : entry=10842
UPDATE `trans_words` SET `content_loc4`='The totem has various primal markings. You see owls, bears, wolves and stags carved into the surface of the totem.$B$BYou note a strange collection of symbols that makes your mouth form the word C-O-O.' WHERE `word_id` = 147404; -- locales_quest - offerrewardtext : entry=9539
UPDATE `trans_words` SET `content_loc4`='The totem has various primal markings. You see owls, bears, wolves and stags carved into the surface of the totem.$B$BYou note a strange collection of symbols that makes your mouth form the word T-I-K-T-I.' WHERE `word_id` = 147407; -- locales_quest - offerrewardtext : entry=9540
UPDATE `trans_words` SET `content_loc4`='The totem has various primal markings. You see owls, bears, wolves and stags carved into the surface of the totem.$B$BYou note a strange collection of symbols that makes your mouth form the word Y-O-R.' WHERE `word_id` = 147410; -- locales_quest - offerrewardtext : entry=9541
UPDATE `trans_words` SET `content_loc4`='The totem has various primal markings. You see owls, bears, wolves and stags carved into the surface.$B$BYou note a strange collection of symbols that makes your mouth form the word A-K-I-D-A.' WHERE `word_id` = 147400; -- locales_quest - offerrewardtext : entry=9538
UPDATE `trans_words` SET `content_loc4`='The tower and surrounding areas should now be relatively secure, though only for the time being. You have done well in providing us with a buffer of security, but we will need to reassert control over the entire isle if we are to survive here in the long run. This will involve tackling much greater threats than errant mana wyrms and lynxes.$B$BTake this, $N - you will no doubt make good use of it for the tasks to come.' WHERE `word_id` = 145913; -- locales_quest - offerrewardtext : entry=8326
UPDATE `trans_words` SET `content_loc4`='The tracks lead south from Imarion\'s equipment, appearing to follow the Southern Rampart. The roughness of the shallow ruts suggests that Imarion\'s captors were in a hurry to move him to his next destination, but why?$B$BWas someone waiting there for him, perhaps the Warboss Nekrogg, the leader named in the orders you found further north?$B$BThe shield and the tracks raise more questions than they solve, but it seems certain that where you find Private Imarion, you\'re likely to find Warboss Nekrogg too.' WHERE `word_id` = 149762; -- locales_quest - details : entry=10128
UPDATE `trans_words` SET `content_loc4`='The tracks lead south from Makha\'s equipment, appearing to cross the Path of Glory. The roughness of the shallow ruts suggests that Makha\'s captors were in a hurry to move her to the next destination, but why?$B$BWas someone waiting there for her, perhaps Warboss Nekrogg, the leader named in the orders you found further north?$B$BThe shield and the tracks raise more questions than they solve, but it seems certain that where you find Scout Makha, you\'re likely to find Warboss Nekrogg too.' WHERE `word_id` = 149855; -- locales_quest - details : entry=10153
UPDATE `trans_words` SET `content_loc4`='The Traitor Uncovered' WHERE `word_id` = 147371; -- locales_quest - ObjectiveText1 : entry=9531
UPDATE `trans_words` SET `content_loc4`='The trees of this land are ravaged by the power that was unleashed from Auchindoun. The foul magic that was unleashed blasted the land into the wastes you see all around. The trees that survived are now infested with wretched wood mites that crawl under their bark and gnaw at their flesh. $B$BRotting forest-rages and infested root-walkers are breeding grounds for the mites. They are found along the edge of the wood to the east. Destroy these creatures and then kill the mites that flee their husks.' WHERE `word_id` = 152813; -- locales_quest - details : entry=10896
UPDATE `trans_words` SET `content_loc4`='The Triumvirate has decided to try Matis immediately. You have brought an arch-criminal to justice on this day, young $R.$B$BYou have performed admirably for two of three; only Aesom remains.' WHERE `word_id` = 272620; -- locales_quest - offerrewardtext : entry=9711
UPDATE `trans_words` SET `content_loc4`='The trolls have been keeping us pinned here while planning an attack on Fairbreeze Village. We were not expecting this at all.' WHERE `word_id` = 146735; -- locales_quest - offerrewardtext : entry=9360
UPDATE `trans_words` SET `content_loc4`='The troops of the Shattered Sun Offensive will need every advantage they can get to beat back the allies of Kil\'Jaeden. My colleagues in Stormwind have created oils which will strengthen the blades and armor of our troops.$B$BThe key ingredient, nether residue, pervades Outland  -- if you know where to look. Any master herbalist, skinner, or miner who knows the signs can gather it while plying $G his:her; trade. Those herbs, ores, and beasts unique to Outland may all bear the residue.' WHERE `word_id` = 154666; -- locales_quest - details : entry=11875
UPDATE `trans_words` SET `content_loc4`='The Unyielding have felt the strength of the Horde. No doubt they will soon be in the mood to listen to my proposed alliance.' WHERE `word_id` = 150132; -- locales_quest - offerrewardtext : entry=10220
UPDATE `trans_words` SET `content_loc4`='The Vekh\'nir are the least of our adversaries.$B$BSo, you know how to slay an overgrown bird. This does not make you a true warrior. This does not give you a full understanding of our ways.$B$BBut it\'s a start.' WHERE `word_id` = 152586; -- locales_quest - offerrewardtext : entry=10846
UPDATE `trans_words` SET `content_loc4`='The Violet Eye? You don\'t say.$B$BShow me this report you speak of.' WHERE `word_id` = 148562; -- locales_quest - offerrewardtext : entry=9829
UPDATE `trans_words` SET `content_loc4`='The voidstone reveals a vision of a multi-story building haunted by ghostly figures. As the scene sharpens and blurs, other buildings come into view, leading you to believe it is Goldenmist Village.$B$BRooftops and tree canopies suddenly flash by, revealing the way to Goldenmist by following the Elrendar River to the west.$B$BOnce there, use the voidstone near the rune of summoning atop the center building and defeat the summoned voidwalker. Only then can you return to your teacher.' WHERE `word_id` = 147711; -- locales_quest - details : entry=9619
UPDATE `trans_words` SET `content_loc4`='The warchief has been waiting for word of this ever since we set foot on Outland. Now you get the honor of giving him the news of the Mag\'har yourself.$B$BGo quickly, $N. Take this letter with you; it bears my personal seal. There is nothing that Thrall wants to hear more than the message you bear.' WHERE `word_id` = 146997; -- locales_quest - details : entry=9438
UPDATE `trans_words` SET `content_loc4`='The wards you encountered are powerful magic, indeed. The techniques employed by the arakkoa are so far beyond our understanding that we can only hope to find a counter with their help.$B$BI know what\'s going through your mind. Why would an enemy help us to overcome their defenses? The answer is that they wouldn\'t, of course. Have you heard the tales of a crazy old arakkoa sage named Kokorek? They say he\'s hiding in Veil Lithic on the western edge of the Bone Wastes, waiting for the return of a lost age.' WHERE `word_id` = 149113; -- locales_quest - details : entry=9963
UPDATE `trans_words` SET `content_loc4`='The Warmaul Champion Defeated' WHERE `word_id` = 149151; -- locales_quest - EndText : entry=9973
UPDATE `trans_words` SET `content_loc4`='The wild sparrowhawks of Nagrand have the keenest sight of all the predatory birds here. But, because of their size, they\'ve been pushed out of most areas by the larger windrocs.$B$BMost of the sparrowhawks nest here, on the Twilight Ridge. If you can catch one, I\'ll train it to spot the pieces of your buried tablet.$B$BIt\'s going to be difficult, though. The sparrowhawks are very skittish and will flee at your approach. I\'d suggest cornering one or prowling to get close enough to cast your net.' WHERE `word_id` = 153194; -- locales_quest - details : entry=10987
UPDATE `trans_words` SET `content_loc4`='The Winterfall are known to carry spirit beads to ward against foul spirits. These beads have clearly failed in their purpose, as the Winterfall themselves are the agents of corruption. Bring to me these spirit beads from fallen Winterfall furbolgs; for every set of five you bring me, you will earn recognition amongst the Timbermaw.' WHERE `word_id` = 143596; -- locales_quest - objectives : entry=8469
UPDATE `trans_words` SET `content_loc4`='The writing on the plans is far too delicate and precise to have come from any of the fel orcs. You can\'t make out what the text says, but the implications are pretty clear: the fel orcs are taking orders from the blood elves at Firewing Point.$B$BWhy would the two forces be working together? Rokag might have some insight into this odd alliance. You decide to make your way back to Stonebreaker Hold and show him the map.' WHERE `word_id` = 149304; -- locales_quest - details : entry=10013
UPDATE `trans_words` SET `content_loc4`='The Zephyrium Capacitorium is ready for its longest launch yet!  What do you say, $N?  Do you want to fly one more time?$B$BI\'ve been boosting the proplusion system but, until I get all the variables hammered out, the capicitorium will need added fuel to give it the necessary kick!  Speak with Rally before the flight -- he has the details.$B$BBest of luck, $N. This flight will take you to distant Raven\'s Wood. If you survive the flight -- and the wood\'s ogres and arakkoa -- come back and we\'ll celebrate!' WHERE `word_id` = 152091; -- locales_quest - details : entry=10716
UPDATE `trans_words` SET `content_loc4`='Theramore Prisoners Freed' WHERE `word_id` = 153823; -- locales_quest - ObjectiveText1 : entry=11145
UPDATE `trans_words` SET `content_loc4`='There are so few that are gifted enough to be able to channel such power from M\'uru. To see this in a child is truly amazing!$B$BIt was wise of you to bring her before me.$B$BYou say that she\'s an orphan in Shattrath City?  I shall see to it that a petition is submitted to the orphan matron there so that she might be remanded to our custody for proper training. She belongs with us, here.$B$BSee to it that you keep her safe until then.' WHERE `word_id` = 273589; -- locales_quest - offerrewardtext : entry=10960
UPDATE `trans_words` SET `content_loc4`='There are so many that we can never kill them all, but you have given me hope. Perhaps I shall try a little harder for another few centuries to revive my beloved wood.' WHERE `word_id` = 152263; -- locales_quest - offerrewardtext : entry=10770
UPDATE `trans_words` SET `content_loc4`='There he is!  I\'d know that scar anywhere.$B$BTo be completely honest, I thought he\'d be... bigger. Well, I suppose I\'ll let him go and try again another day.' WHERE `word_id` = 154616; -- locales_quest - offerrewardtext : entry=11667
UPDATE `trans_words` SET `content_loc4`='There is a camp of Deadwood furbolgs directly to our southwest. Corruption permeates the camp, and I wish to put an end to it before they become a problem.$B$BThe Deadwood tribe is evil, but it is the effects of the fel that pollutes their minds. Their hostility is not truly an innate quality, yet they cannot be helped. For this reason, I am deeply saddened by having to fight my own brethren.$B$BPlease, $N, do what must be done, and do not mention the details; they upset me far too much.' WHERE `word_id` = 145799; -- locales_quest - details : entry=6221
UPDATE `trans_words` SET `content_loc4`='There is a second production facility, Forge Camp: Rage, that lies to the immediate northeast of Thrallmar. The demons have positioned a number of their fel cannons along the ridge - and they\'re aimed straight at us! You must enter the camp and destroy these fel cannons. $B$B$N, you must be sure to detonate them all - or nothing will stop the Legion from raining fiery hell down upon our heads!  ' WHERE `word_id` = 273103; -- locales_quest - details : entry=10391
UPDATE `trans_words` SET `content_loc4`='There is always more work that must be done, $N. Now seek out a stasis chamber and destroy whatever evil lurks within!' WHERE `word_id` = 153147; -- locales_quest - offerrewardtext : entry=10975
UPDATE `trans_words` SET `content_loc4`='There is no accounting for taste. Take my most recent buyer for instance.$B$BThere is a blood orc within Hellfire Citadel known as Keli\'dan the Breaker. He bears a most mundane stave; certainly nothing to take a second glance at. And yet, this is what my customer wishes.$B$BWho am I to say no?$B$BIf you desire my compensation, you will find him and his stave within the Blood Furnace.' WHERE `word_id` = 154174; -- locales_quest - details : entry=11362
UPDATE `trans_words` SET `content_loc4`='There is no greater honor than driving invaders from Horde territories. We salute your great courage in aiding our forces today!' WHERE `word_id` = 155993; -- locales_quest - offerrewardtext : entry=11340
UPDATE `trans_words` SET `content_loc4`='There is one more gronn to be slain: Skulloc Soulgrinder. He keeps himself hidden away, but there is a way to coax him forth.$B$BHe was trained by an ogre warlock known as Vim\'gol the Vile, whose grimoire you must retrieve!$B$BVim\'gol must be summoned by yourself, and four others, each standing within a different fire ring at his circle. Find Vim\'gol\'s Circle in the mountains, to the northeast, near the Broken Wilds.$B$BAnd, $N, if you hear Vim\'gol talk about growing bigger, get back to the fire rings!' WHERE `word_id` = 153246; -- locales_quest - details : entry=10998
UPDATE `trans_words` SET `content_loc4`='There is only ever one great mother of the marsh walkers in Zangarmarsh. She is always known as the Black Stalker, and she hatches from one of the eggs left behind when her mother dies.$B$BI have a buyer who is interested in one of those eggs. He mentioned something about adding her as a zoo attraction.$B$BIf you are interested in the reward I am offering, travel to the Underbog and slay the Black Stalker for her egg.' WHERE `word_id` = 154194; -- locales_quest - details : entry=11369
UPDATE `trans_words` SET `content_loc4`='There is strength in numbers, $N. Being part of the "Brew of the Month" club means knowing you and your brethren will always have access to the best brew.$B$BBring your "Brew of the Month" club membership form to Ray\'ma in the Darkbriar Lodge in Orgrimmar\'s Valley of Spirits.' WHERE `word_id` = 154789; -- locales_quest - details : entry=12306
UPDATE `trans_words` SET `content_loc4`='There, that\'s better. I\'ve added a little fel magic to it.$B$BNow, you must take it to his resting place in the Western Plaguelands. Use the corrupted holy symbol in the presence of Uther\'s statue to defile his tomb!  You\'ll have to get close for it to work.$B$BTo find the tomb go southeast of the Ruins of Andorhal. Return to me when the deed is done.$B$BBe quick about it, $C!' WHERE `word_id` = 147029; -- locales_quest - details : entry=9444
UPDATE `trans_words` SET `content_loc4`='There\'s a dwarf in Gadgetzan by the name of Derotain Mudsipper who\'ll teach you how to make imperial plate armor. All you gotta do is get to the Tanaris desert and find him. Interested?' WHERE `word_id` = 152791; -- locales_quest - details : entry=10891
UPDATE `trans_words` SET `content_loc4`='There\'s is no cause of fury greater in this world than that of Gruul and his sons. They slaughtered my people - my children - and now I will have revenge.$B$BI have fashioned a trap from dragon fire, and if you\'re clever enough, you can use it to kill even a gronn.$B$BJust hurry, take the trap to Rexxar. My thirst for revenge must be quenched.' WHERE `word_id` = 152324; -- locales_quest - details : entry=10785
UPDATE `trans_words` SET `content_loc4`='There\'s more than enough here for my research. Thank you, $N. If you find any other evidence that the crystals are corrupting Bloodmyst\'s native creatures, let me know right away.' WHERE `word_id` = 147539; -- locales_quest - offerrewardtext : entry=9574
UPDATE `trans_words` SET `content_loc4`='There\'s no scrying necessary to see the trouble my new "neighbors" are creating. And if this is the band that burnt down the Shady Rest Inn, as you claim, there\'s no reason to tolerate their presence any longer.$B$BTake this torch and raze their camp to the ground! You\'ll find Direhorn just to the north of my farm. You can\'t miss it, really, as the Grimtotems have done a splendid job of announcing their presence with those totems they favor.' WHERE `word_id` = 153846; -- locales_quest - details : entry=11150
UPDATE `trans_words` SET `content_loc4`='There\'s nothing here that would explain why the spirits of these arakkoa have remained in this world or how they came into thirst for the blood of orcs.$B$BI think our best lead is to try to find the deserter mentioned in the journal. If he left his comrades at the camp, there\'s a chance you might be able to persuade him to help us understand what the dark conclave arakkoa are planning.$B$BStart your search for him on the road through the Magma Fields to the southeast.' WHERE `word_id` = 152227; -- locales_quest - details : entry=10761
UPDATE `trans_words` SET `content_loc4`='There\'s nothing more annoying than preparing to cast a spell, only to have it interrupted by some unseen force. After the fourth time, I knew it couldn\'t be coincidence and I sent Keb\'ezil out to investigate.$B$BI guarantee you\'ll never encounter an imp that talks back more than Keb.$B$BAnyway, he managed to find the source of the problem, a pesky creature known as a warp stalker. They have a habit of disturbing the magical energy wherever they blink into existence. I\'ve had enough of their interference.' WHERE `word_id` = 149364; -- locales_quest - details : entry=10027
UPDATE `trans_words` SET `content_loc4`='There\'s truth to the rumors! This hammer is light and strong at once; I shall manage to complete several of my unfinished projects. And of course, I\'ll let you have first pick.' WHERE `word_id` = 146028; -- locales_quest - offerrewardtext : entry=8477
UPDATE `trans_words` SET `content_loc4`='These are all in excellent state, $N. Carry on and you will soon count the Scryers as your friends.' WHERE `word_id` = 149354; -- locales_quest - offerrewardtext : entry=10024
UPDATE `trans_words` SET `content_loc4`='These are good hides, Maktu thinks. They will make very good armor.$B$BWho knows? There might even be enough left over for Maktu to make some armor for the draenei.' WHERE `word_id` = 148585; -- locales_quest - offerrewardtext : entry=9834
UPDATE `trans_words` SET `content_loc4`='These are good spores and they should keep us going for quite some time. Thank you for helping us to replenish our supplies, $N.' WHERE `word_id` = 148323; -- locales_quest - offerrewardtext : entry=9777
UPDATE `trans_words` SET `content_loc4`='These are great spores, $N!  Tallstalks are vital to rebuilding our town. And after all the bog lord attacks we\'ve suffered, we\'re in quite the short supply.' WHERE `word_id` = 148463; -- locales_quest - offerrewardtext : entry=9806
UPDATE `trans_words` SET `content_loc4`='These are magnificent, utterly magnificent! I\'ll pay you top coin for these! How much to retain your services?' WHERE `word_id` = 151033; -- locales_quest - offerrewardtext : entry=10433
UPDATE `trans_words` SET `content_loc4`='These are perfect!  Oh, thank you, $C!$B$BYou\'ve been ever so careful not to disturb the natural order of things, and we\'ve gathered so much information in such a short amount of time!$B$BNow, let\'s see if our little plan to grow a new food supply for the bog lords is going to work.' WHERE `word_id` = 148051; -- locales_quest - offerrewardtext : entry=9709
UPDATE `trans_words` SET `content_loc4`='These are perfect, $N! I could not have asked for better.' WHERE `word_id` = 149327; -- locales_quest - offerrewardtext : entry=10018
UPDATE `trans_words` SET `content_loc4`='These are remarkably powerful, $N. I\'m very pleased with your work.' WHERE `word_id` = 146327; -- locales_quest - offerrewardtext : entry=9150
UPDATE `trans_words` SET `content_loc4`='These be excellent, mon!$B$BI\'ll tell you what. Since you been a good sport and helped with the mushrooms, I\'ll let you in on the fun!' WHERE `word_id` = 148490; -- locales_quest - offerrewardtext : entry=9814
UPDATE `trans_words` SET `content_loc4`='These be fine ones, $N. Here\'s your payment.$B$B<Magasha bends one of the wings and holds it up to her head.>$B$BNope, not my thing, but who\'m I to complain?$B$B<Magasha shrugs.>$B$BCoin speaks.' WHERE `word_id` = 148285; -- locales_quest - offerrewardtext : entry=9769
UPDATE `trans_words` SET `content_loc4`='These blood crystals that you saw; my scouts have seen many more to the north.$B$BI thank you for aid but now ask that you leave me to confer with the elders. I will call for you once I have spoken with the other elders of the village.' WHERE `word_id` = 147504; -- locales_quest - offerrewardtext : entry=9566
UPDATE `trans_words` SET `content_loc4`='These blood elves mean to kill us all. How are we to divert them from this course?' WHERE `word_id` = 150528; -- locales_quest - offerrewardtext : entry=10303
UPDATE `trans_words` SET `content_loc4`='These insignia are a good start.$B$BUnfortunately while you were at the Heap, one of my other agents amongst the Zaxxis sent word to me that Warp-Raider Nesaad still lives!' WHERE `word_id` = 150334; -- locales_quest - offerrewardtext : entry=10262
UPDATE `trans_words` SET `content_loc4`='These islands will serve as the home to thousands. Most of these people will remain here their entire lives. If you so desired, you could do the same. But that\'s not what you want, is it, $N?$B$B<Velen nods.>$B$BYou will do many more great things before you pass. You must embrace your destiny and meet it head on!$B$BBefore you leave, I present you with two gifts: This tabard is worn by only the most revered of the Hand of Argus and this signet ring worn by our wisest sages. Wear them both with pride, $N.' WHERE `word_id` = 148262; -- locales_quest - offerrewardtext : entry=9762
UPDATE `trans_words` SET `content_loc4`='These look like fine specimens. Thank you for performing the field work. Once I can find the time to analyze these, I should be able to tell what it will take to restore the fields.' WHERE `word_id` = 148425; -- locales_quest - offerrewardtext : entry=9799
UPDATE `trans_words` SET `content_loc4`='These mana cells are vital to our efforts. We will have the portal to the Sunwell Isle created in no time!$B$BReturn tomorrow and I will send you out to acquire more of them from our Bash\'ir \'friends\'.' WHERE `word_id` = 154445; -- locales_quest - offerrewardtext : entry=11513
UPDATE `trans_words` SET `content_loc4`='These mana cells will continue to power the portal.$B$BReturn tomorrow and I will send you out to obtain more.' WHERE `word_id` = 154449; -- locales_quest - offerrewardtext : entry=11514
UPDATE `trans_words` SET `content_loc4`='These notes... are shocking. If they\'re correct it means the damage to the land on the Dead Scar is irreversible.$B$BI\'ll notify the grand magister right away. More reason for our race to escape this world and find our true fate in Outland.' WHERE `word_id` = 146537; -- locales_quest - offerrewardtext : entry=9255
UPDATE `trans_words` SET `content_loc4`='These orcs have never seen a tauren before - if you can believe such a thing. If it weren\'t for Captain Kroghan, they would have probably been serving me for their next meal right about now.$B$BBut where are my manners? I am Ohlorn Farstrider, hunter extraordinaire! I am part of the Nesingwary expedition that came up here from Azeroth in search of big game. If you are interested in the hunt, perhaps you should visit my crew at the Nesingwary Safari, due east of here near the border of Zangarmarsh.' WHERE `word_id` = 272915; -- locales_quest - details : entry=10114
UPDATE `trans_words` SET `content_loc4`='These ought to do nicely. Don\'t they stink, though?$B$BI hope that this is worth your troubles... I\'m just glad that it was you collecting this stuff instead of me!' WHERE `word_id` = 151941; -- locales_quest - offerrewardtext : entry=10671
UPDATE `trans_words` SET `content_loc4`='These readings do not bode well, $N. Karazhan should be emanating all manner of arcane currents... instead it has become a huge energy vacuum. No signals. Nothing. Except for a very faint demonic echo.$B$BThis is bad news, $N.' WHERE `word_id` = 148540; -- locales_quest - offerrewardtext : entry=9824
UPDATE `trans_words` SET `content_loc4`='These results match Mehrdad\'s exactly, but why? The only possible explanation I can think of is that some of the dome generation equipment has failed or gone missing. That would cause the domes to weaken significantly and fall out of balance as the diagnostics indicate.' WHERE `word_id` = 150988; -- locales_quest - offerrewardtext : entry=10424
UPDATE `trans_words` SET `content_loc4`='These seem sturdy enough to do the job. Thank you for your help, $N. Your contribution to Blood Watch\'s defense will not be forgotten.' WHERE `word_id` = 147821; -- locales_quest - offerrewardtext : entry=9643
UPDATE `trans_words` SET `content_loc4`='These should be more than sufficient, $N. They seem to be fixated on birds, which is odd, because there aren\'t any bird species native to Zangarmarsh. What could this mean?$B$BCould they have encountered the arakkoa? To a shamanistic people, they might seem like the embodiment of a powerful bird spirit.' WHERE `word_id` = 148372; -- locales_quest - offerrewardtext : entry=9787
UPDATE `trans_words` SET `content_loc4`='These should do nicely, $C.$B$BI thank you for your assistance.' WHERE `word_id` = 154470; -- locales_quest - offerrewardtext : entry=11520
UPDATE `trans_words` SET `content_loc4`='These should do the job. I was concerned that the rangers might be short on arrows for a while, but that\'s one less thing I have to worry about. Thank you for your help, $C.' WHERE `word_id` = 146794; -- locales_quest - offerrewardtext : entry=9381
UPDATE `trans_words` SET `content_loc4`='These should do the job. I\'m going to prepare the salve and finish my splint before making my way back to Thrallmar. Why do I have the feeling that I know what Far Seer Regulkut will say when she sees me?$B$B<Grelag sighs half-heartedly.>$B$BThank you for your help, $N. I have some of the spineleaf left over. You\'re welcome to take it with you. Who can tell when it will prove useful?' WHERE `word_id` = 146692; -- locales_quest - offerrewardtext : entry=9345
UPDATE `trans_words` SET `content_loc4`='These should keep me supplied for some time to come! I think some of the others have begun to take notice. I\'ve had several offers to buy my blades already. Selling them on the side might provide better business than my customary goods.' WHERE `word_id` = 148622; -- locales_quest - offerrewardtext : entry=9842
UPDATE `trans_words` SET `content_loc4`='These should work nicely. Now, let\'s forge them into a more useful form.' WHERE `word_id` = 151763; -- locales_quest - offerrewardtext : entry=10626
UPDATE `trans_words` SET `content_loc4`='These signs do not bode well for us, $N. I\'ve never been one for secrecy so I will tell you a bit about my organization, the Violet Eye.$B$BWe are agents of the Kirin Tor, the Magus Senate of Dalaran. We were assigned to keep an eye on Karazhan and Medivh, in case things got out of hand.$B$BWhich they did... very quickly, after Medivh disappeared.$B$BThis report details my findings. Take it to Archmage Cedric, also from the Violet Eye; you will find him in the outskirts of Dalaran.' WHERE `word_id` = 148550; -- locales_quest - details : entry=9826
UPDATE `trans_words` SET `content_loc4`='These stones carry a taint I do not wish to remember, but one I cannot forget. You try my patience dangerously by bringing these to me.$B$BHowever, it is clear your heart seeks the same vengeance as I, and I will aid you with instruction.' WHERE `word_id` = 151625; -- locales_quest - offerrewardtext : entry=10592
UPDATE `trans_words` SET `content_loc4`='These will do nicely, but we can always use more. Perhaps you will find yourself in the Underbog again soon?' WHERE `word_id` = 148067; -- locales_quest - offerrewardtext : entry=9715
UPDATE `trans_words` SET `content_loc4`='These will do perfectly, $N. Bring any more armaments you find. Our need is great and our resources are scarce.' WHERE `word_id` = 148275; -- locales_quest - offerrewardtext : entry=9765
UPDATE `trans_words` SET `content_loc4`='These will do perfectly, $N. Take this piece of armor as a reward. I\'ll be able to make more with the pelts you brought me.' WHERE `word_id` = 146077; -- locales_quest - offerrewardtext : entry=8491
UPDATE `trans_words` SET `content_loc4`='These will do quite nicely. Allow me a moment to mix up the potion.' WHERE `word_id` = 148435; -- locales_quest - offerrewardtext : entry=9801
UPDATE `trans_words` SET `content_loc4`='These will do. Thank you, $C!$B$BWhile you were out, I managed to gather some equipment together to make an analysis of the samples. This should only take a moment.' WHERE `word_id` = 146621; -- locales_quest - offerrewardtext : entry=9293
UPDATE `trans_words` SET `content_loc4`='These will have to do for now, I suppose. If only we could scare them off.$B$BHey, that gives me an idea!' WHERE `word_id` = 150202; -- locales_quest - offerrewardtext : entry=10234
UPDATE `trans_words` SET `content_loc4`='These will work quite nicely, $N. Thank you for agreeing to help us.$B$BBefore we can continue, however, there is something else we\'ll need to address.' WHERE `word_id` = 150379; -- locales_quest - offerrewardtext : entry=10272
UPDATE `trans_words` SET `content_loc4`='They call him Cho\'war the pillager, $N. He\'s the ogre that forced out the gronn that used to rule over these ogres. You better believe he\'s powerful!$B$BThe chef said that Cho\'war lives in the big cave - wherever that may be... Get that key and get me out of here! If we live through this, my father will no doubt give you the big reward! As soon as I\'m free, seek him out.' WHERE `word_id` = 149084; -- locales_quest - details : entry=9955
UPDATE `trans_words` SET `content_loc4`='THEY HAVE A PIT LORD?!$B$B<Nazgrel notices the glances of the others assembled and lowers his voice to a whisper.>$B$BThat seals it. They must be using the blood from this pit lord to create new fel orcs that are somehow not aligned with the Burning Legion. And if they can do this to the Mag\'har brown orcs, they can do it to us!$B$BWe\'ll have to strike at the heart of their military might, and soon!' WHERE `word_id` = 147682; -- locales_quest - offerrewardtext : entry=9608
UPDATE `trans_words` SET `content_loc4`='THEY HAVE A PIT LORD?!$B$B<The force commander looks around at the stares of the others and then lowers his voice to a whisper.>$B$BThat seals it. They must be using the blood from this pit lord to create new fel orcs that are somehow not aligned with the Burning Legion. And if they can do this to the brown orcs, they can do it to the green ones too. With all of the new Horde orcs streaming in through the Dark Portal, that\'s not good!$B$BWe must strike at the heart of their military might, and soon!' WHERE `word_id` = 147677; -- locales_quest - offerrewardtext : entry=9607
UPDATE `trans_words` SET `content_loc4`='They refused? They are welcome to choose their own path, of course, but their choice surprises me. I would think that all the draenei, including the Broken and Lost Ones, would still remember that we\'re all a part of the same people.$B$BI suppose he is right about the folly of deceiving them, but still, could he not see that our intentions were friendly?' WHERE `word_id` = 148445; -- locales_quest - offerrewardtext : entry=9803
UPDATE `trans_words` SET `content_loc4`='They seek to summon a frost lord into our world? No! They cannot tip the balance!' WHERE `word_id` = 274187; -- locales_quest - offerrewardtext : entry=11891
UPDATE `trans_words` SET `content_loc4`='They\'re building a second mana bomb in the courtyard. My intel suggests that the final code for the bomb is being held by Sharth Voldoun, the overseer of the project at Firewing Point.$B$B$N, you have to go back in there and retrieve the final code from him. He keeps himself to the top of the highest spire.$B$BTake the final code to the mana bomb that they\'re working on in the central courtyard and set it off.$B$BOnly then will we be certain that Stonebreaker Hold is safe.' WHERE `word_id` = 151092; -- locales_quest - details : entry=10447
UPDATE `trans_words` SET `content_loc4`='They\'re gone now. Scared the breeches out of them with a well-placed fireball! Unfortunately for me, they threw our precious cargo into the river.' WHERE `word_id` = 146215; -- locales_quest - offerrewardtext : entry=9035
UPDATE `trans_words` SET `content_loc4`='They\'ve got me locked up good this time, $N. The Warmaul king, Cho\'war, has the key.' WHERE `word_id` = 149083; -- locales_quest - offerrewardtext : entry=9954
UPDATE `trans_words` SET `content_loc4`='Things are starting to look up. I think if we can prevent the fel orcs from reinforcing their fellows here, we can keep this post under control.' WHERE `word_id` = 149278; -- locales_quest - offerrewardtext : entry=10007
UPDATE `trans_words` SET `content_loc4`='Thirty orcs, $R! Of those original thirty, only Ungriz and I remain. We found the young orc, Saurfang, nearly dead near Sunspring. Apparently he was here for another reason...' WHERE `word_id` = 335191; -- locales_quest - offerrewardtext : entry=9944
UPDATE `trans_words` SET `content_loc4`='This book... it can\'t be!  It\'s Dar\'Khan\'s very own handwriting. There must be information here that will reveal a weakness in our opponent.$B$BYou\'ve done well in coming to us, $N. The Farstriders have shown great wisdom in leaving this matter in our hands. This armor should help you further our cause.' WHERE `word_id` = 146421; -- locales_quest - offerrewardtext : entry=9172
UPDATE `trans_words` SET `content_loc4`='This calls for immediate action!$B$BWe have someone out in the field closer to Firewing Point that I want you to speak with. She\'ll have a better idea of what our next step should be.$B$BReport to Sergeant Chawni out at Stonebreaker Camp and apprise her of the situation.$B$BTake the road north out of the hold. From the middle of Tuurem, follow the road to the east. You\'ll find the camp on the bank of the river that lies just southwest of Firewing Point.$B$BGood luck, $N.' WHERE `word_id` = 151097; -- locales_quest - details : entry=10448
UPDATE `trans_words` SET `content_loc4`='This cannot be... this belonged to a talonpriest once. How did you ever come across it?' WHERE `word_id` = 153352; -- locales_quest - offerrewardtext : entry=11024
UPDATE `trans_words` SET `content_loc4`='This Crimson Sigil will be investigated. They pose a threat to all life on Draenor. I foresee a day when their dark master has to answer for his crimes. Perhaps it will be you who brings him to justice, hero.' WHERE `word_id` = 152307; -- locales_quest - offerrewardtext : entry=10781
UPDATE `trans_words` SET `content_loc4`='This crystal shard will give our researchers a baseline to compare against samples from other parts of the island, since it comes from one of Bloodmyst\'s less corrupted regions.$B$BI won\'t be able to send it off until we have a full set of samples, but this is a great start. If you\'re willing to help gather the other samples, I\'ll see to it that you\'re rewarded for your efforts.' WHERE `word_id` = 147567; -- locales_quest - offerrewardtext : entry=9581
UPDATE `trans_words` SET `content_loc4`='This defeat is not only a blow to Illidan. The Legion too reels... A pit lord commander has been defeated. The siege of Hellfire Citadel now holds no purpose for the Burning Legion. All of their efforts will undoubtedly be turned upon the Black Temple.$B$BRedemption comes for Illidan... and soon.' WHERE `word_id` = 153263; -- locales_quest - offerrewardtext : entry=11002
UPDATE `trans_words` SET `content_loc4`='This dust is as potent as ever, $N. Keep bringing it to me if you\'re in need to boost your magical powers against the Scourge.' WHERE `word_id` = 146505; -- locales_quest - offerrewardtext : entry=9219
UPDATE `trans_words` SET `content_loc4`='This fire seems new... Yet, you say it came from the Exodar? Fascinating! To make your way into that ship... Of course, I\'ll pay you for this. If you find anything else like this, be sure to speak with me again.' WHERE `word_id` = 274216; -- locales_quest - offerrewardtext : entry=11933
UPDATE `trans_words` SET `content_loc4`='This flame, stolen from the heart of Stormwind itself, floods you with warmth. This raw power is foreign to you, but the (NAME) might know more...' WHERE `word_id` = 272395; -- locales_quest - details : entry=9339
UPDATE `trans_words` SET `content_loc4`='This flame, stolen from the heart of the Exodar itself, floods you with warmth. This raw power is foreign to you, but the Talespinner might know more...' WHERE `word_id` = 154717; -- locales_quest - details : entry=11933
UPDATE `trans_words` SET `content_loc4`='This has the information I\'m looking for. Just a couple more tweaks, and we\'ll have you taking control of a fel reaver in no time.' WHERE `word_id` = 151702; -- locales_quest - offerrewardtext : entry=10611
UPDATE `trans_words` SET `content_loc4`='This head will be posted at the gates of Garadar for all to see. The ogres will think twice before interrupting sacred Mag\'hari rituals.' WHERE `word_id` = 149051; -- locales_quest - offerrewardtext : entry=9946
UPDATE `trans_words` SET `content_loc4`='This information is very valuable, $N. We all knew about Dar\'Khan\'s plans to steal the power of the Sunwell for the Scourge, but much of the information found here is completely new to us. Magister Kaendris at the Sanctum of the Sun will want to look at this.' WHERE `word_id` = 146374; -- locales_quest - offerrewardtext : entry=9162
UPDATE `trans_words` SET `content_loc4`='This information will be every helpful, $N. I will further refine that design and see if we can\'t expand its utility.' WHERE `word_id` = 151502; -- locales_quest - offerrewardtext : entry=10566
UPDATE `trans_words` SET `content_loc4`='This is a good first step toward securing our position here against the ogres.$B$BWith their numbers decimated, the Bladespire will think twice about attempting to retake Thunderlord Stronghold.$B$BNow, if they were the only clan of ogres that we have to worry about.' WHERE `word_id` = 151260; -- locales_quest - offerrewardtext : entry=10503
UPDATE `trans_words` SET `content_loc4`='This is a good first step toward securing Sylvanaar and the grove.$B$BWith their ogres not so numerous, the Bloodmaul will think twice about attacking us here.$B$BNow, if only they were the only clan of ogres that we have to worry about.' WHERE `word_id` = 151254; -- locales_quest - offerrewardtext : entry=10502
UPDATE `trans_words` SET `content_loc4`='This is a great victory for the people of Garadar! Well done, $N!' WHERE `word_id` = 149028; -- locales_quest - offerrewardtext : entry=9939
UPDATE `trans_words` SET `content_loc4`='This is a great victory for the people of Telaar! Well done, $N!' WHERE `word_id` = 149031; -- locales_quest - offerrewardtext : entry=9940
UPDATE `trans_words` SET `content_loc4`='This is astounding! Not only have you brought back Medivh\'s Journal, but you\'ve also retrieved a fragment of his very essence.$B$BAs faint as this lingering essence is, it will reveal to us vital information about Medivh. We will not forget this, $C.' WHERE `word_id` = 147825; -- locales_quest - offerrewardtext : entry=9644
UPDATE `trans_words` SET `content_loc4`='This is big, $N. Very big!$B$BVoren\'thal is going to want to hear about this as soon as possible.' WHERE `word_id` = 151028; -- locales_quest - offerrewardtext : entry=10432
UPDATE `trans_words` SET `content_loc4`='This is even more potent than I expected! With this much power, I could forge a dozen blades!' WHERE `word_id` = 151912; -- locales_quest - offerrewardtext : entry=10665
UPDATE `trans_words` SET `content_loc4`='This is fantastic but I just received some incredible news!' WHERE `word_id` = 151971; -- locales_quest - offerrewardtext : entry=10677
UPDATE `trans_words` SET `content_loc4`='This is fantastic! The theft of my equipment has given me more insight into the nature of these murlocs than I could have possibly gained in weeks of observations!$B$BI know what I\'m going to say in my report already.$B$B\'When faced with the products of a technologically superior culture, the Blacksilt displayed none of the curiosity characteristic of emerging cultures in humanoid species.\'$B$BIt\'s brilliant! The gentlemen in Ironforge are going to love it! I can\'t thank you enough, $N.' WHERE `word_id` = 147435; -- locales_quest - offerrewardtext : entry=9548
UPDATE `trans_words` SET `content_loc4`='This is grave news indeed. We must do everything in our power to stop this war from coming to pass.' WHERE `word_id` = 156061; -- locales_quest - offerrewardtext : entry=12012
UPDATE `trans_words` SET `content_loc4`='This is grave news, $N. The warp energies emanating from the pipelines are dangerously high.$B$BWe must find a way to shut down the manaforges or what little remains of Outland will be shattered into pieces.' WHERE `word_id` = 150570; -- locales_quest - offerrewardtext : entry=10313
UPDATE `trans_words` SET `content_loc4`='This is in better condition than I thought it\'d be. Sure. it\'s going to take some time to work off all the rust. the char. and the like, but I\'ll not complain.' WHERE `word_id` = 149482; -- locales_quest - offerrewardtext : entry=10055
UPDATE `trans_words` SET `content_loc4`='This is incredible! I set out on this journey unsure of whether the wolf spirit would answer our summons and now it feels as though he\'s always been here. Perhaps he was, $N.$B$BPerhaps he was waiting for the orcs to realize what their choices had wrought.' WHERE `word_id` = 152346; -- locales_quest - offerrewardtext : entry=10791
UPDATE `trans_words` SET `content_loc4`='This is interesting, but disturbing. I wonder if Samia knows who this Kolphis Darkscale is?' WHERE `word_id` = 152105; -- locales_quest - offerrewardtext : entry=10719
UPDATE `trans_words` SET `content_loc4`='This is Kargath? Oh, dear. I expected a, a... well, some kind of settlement, some sign of civilization here in the wilderness.$B$BThere must be a mistake; surely this hovel full of uncouth, unwashed, sniggering savages can\'t be the headquarters of the Expeditionary Force. The loutish barbarians had the gall to laugh at me when I told them about how the Dark Iron dwarves robbed me and then disappeared into the Angor Fortress. I can count on you to help me recover my possessions and my dignity, can\'t I?' WHERE `word_id` = 147002; -- locales_quest - details : entry=9439
UPDATE `trans_words` SET `content_loc4`='This is most curious, $C. I don\'t believe we\'ve ever seen the murlocs wearing any kind of adornment before.$B$BGiven the corrupting influence that the crystal shards are known to have, the murloc that wore this pendant must have believed it granted him power over his fellows. I wouldn\'t be surprised if he had rapidly outgrown his fellow murlocs and gained in strength.' WHERE `word_id` = 147548; -- locales_quest - offerrewardtext : entry=9576
UPDATE `trans_words` SET `content_loc4`='This is no time for celebration, $N. We\'re not out of the fire yet. I\'ve got new orders for you.' WHERE `word_id` = 151650; -- locales_quest - offerrewardtext : entry=10598
UPDATE `trans_words` SET `content_loc4`='This is perfect! In fact, I think it\'s going to work so well, I might just have to redesign the engine to take advantage of all this extra power. If, I mean, when we get out of here, I\'ll have to show my design to everyone back at Area 52!' WHERE `word_id` = 146705; -- locales_quest - offerrewardtext : entry=9351
UPDATE `trans_words` SET `content_loc4`='This is the beginning of a new chapter in blood elf history, $N. You\'ve helped cement the bond between my race and yours, $R.$B$BTake the traitor\'s head to Regent Lord Lor\'themar Theron in Silvermoon City. He\'ll be eager to hear the news.' WHERE `word_id` = 148482; -- locales_quest - details : entry=9811
UPDATE `trans_words` SET `content_loc4`='This is the Coren Direbrew\'s drink?  The brew he wanted more than anything to submit at Brewfest?  Well, officially we can\'t recognize this submission, but out of respect for a fallen brewer, perhaps I\'ll have a taste...' WHERE `word_id` = 274577; -- locales_quest - offerrewardtext : entry=12491
UPDATE `trans_words` SET `content_loc4`='This is the horde version of the black box quest.' WHERE `word_id` = 145647; -- locales_quest - details : entry=3482
UPDATE `trans_words` SET `content_loc4`='This is the ring, is it? Not much to look at, but you know what they say about one elves\' trash...$B$BAnd let\'s not forget your payment. Don\'t feel shy about spending it right here sister!' WHERE `word_id` = 147207; -- locales_quest - offerrewardtext : entry=9491
UPDATE `trans_words` SET `content_loc4`='This is the strongest armor I\'ve ever made! Make good use of it, $N.' WHERE `word_id` = 147525; -- locales_quest - offerrewardtext : entry=9571
UPDATE `trans_words` SET `content_loc4`='This is truly disturbing. The arakkoa were peaceful for a long time. This cult of the Raven was nothing more than a gathering of misfits and madmen.$B$BBut their power has grown, that we cannot discount. Perhaps there is more to this Raven than we know.' WHERE `word_id` = 151683; -- locales_quest - offerrewardtext : entry=10607
UPDATE `trans_words` SET `content_loc4`='This is unsavory business, this use of poisons. But it will do what we want.' WHERE `word_id` = 152178; -- locales_quest - offerrewardtext : entry=10749
UPDATE `trans_words` SET `content_loc4`='This is very troubling. You say that none of the ancestors were at their villages? That they were all moving south?' WHERE `word_id` = 149573; -- locales_quest - offerrewardtext : entry=10085
UPDATE `trans_words` SET `content_loc4`='This is what you found?  That\'s odd. These husked mushroom remains are both unusual and somehow look familiar.$B$BI have to think on this a moment. Don\'t go anywhere!' WHERE `word_id` = 148017; -- locales_quest - offerrewardtext : entry=9702
UPDATE `trans_words` SET `content_loc4`='This is worrisome, friend. It hints at a much larger plot beyond simple agitation of the local fire and water elements, and something here bears investigation...' WHERE `word_id` = 274183; -- locales_quest - offerrewardtext : entry=11886
UPDATE `trans_words` SET `content_loc4`='This is worrisome. The damage is much more severe than I believed. I can send to the Stormspire for some tools to begin repairs, but that\'s useless unless we\'ve isolated what\'s damaging the equipment in the first place.' WHERE `word_id` = 150958; -- locales_quest - offerrewardtext : entry=10417
UPDATE `trans_words` SET `content_loc4`='This is your booterang. There are many like it but this one is yours.$B$BAs an overseer you will use this booterang to sow terror into the hearts of defiant peons. Proper use of the booterang will instill obedience and ensure subservience.$B$BVenture forth to the crystal fields and discipline any peons that you see slacking off. Show them the might of the booterang!' WHERE `word_id` = 273661; -- locales_quest - details : entry=11055
UPDATE `trans_words` SET `content_loc4`='This is... most interesting. And when I say interesting, I mean more disturbing than anything else.$B$BOur efforts to reassert control over the isle in the time following the destruction of the Sunwell have been a challenge. I suspect that whatever foul source is corrupting the isle is at the heart of it all; this sliver may be of some aid in uncovering what\'s really going on.$B$BYou were wise to bring this to me, $N. Take this as compensation for your diligence. Thank you.' WHERE `word_id` = 145947; -- locales_quest - offerrewardtext : entry=8338
UPDATE `trans_words` SET `content_loc4`='This looks a little peculiar. Ghabar definitely didn\'t have Netherstorm in mind when he designed it.$B$BDon\'t look so surprised, $N. Netherstorm is tame compared to some of the environments I\'ve worked in. Nevertheless, a failing dome is always a concern.' WHERE `word_id` = 151019; -- locales_quest - offerrewardtext : entry=10430
UPDATE `trans_words` SET `content_loc4`='This looks like a good assortment. We should be able to find a respectful use for one or two of them.$B$BIn fact, I have an idea for how to do just that!' WHERE `word_id` = 152609; -- locales_quest - offerrewardtext : entry=10851
UPDATE `trans_words` SET `content_loc4`='This may take a while. Take these coins for your hard work.' WHERE `word_id` = 151891; -- locales_quest - offerrewardtext : entry=10660
UPDATE `trans_words` SET `content_loc4`='This may very well be the opportunity that we needed! I will send an attack at once! The Stillpine thank you for your service, $N!' WHERE `word_id` = 147534; -- locales_quest - offerrewardtext : entry=9573
UPDATE `trans_words` SET `content_loc4`='This must be taken back to father at once.' WHERE `word_id` = 151348; -- locales_quest - offerrewardtext : entry=10522
UPDATE `trans_words` SET `content_loc4`='This news you bring from beyond the Dark Portal... you want us to believe the son of Durotan now rules over the lands beyond it... free from any demonic taint?  And that now his people have returned to this world?$B$BI hope for your sake that this isn\'t some manner of joke, $C. What this means to my people... cannot be described. I must send word to Nagrand at once!  Hellscream must hear about this.$B$BTake this letter back to your leader in Thrallmar. Tell him the Mag\'har welcome them!' WHERE `word_id` = 146888; -- locales_quest - details : entry=9406
UPDATE `trans_words` SET `content_loc4`='This object appears to be one of the arcane converters Ley-Keeper Velania mentioned. It hasn\'t been damaged by the fire, perhaps she could find a use for it.' WHERE `word_id` = 146066; -- locales_quest - details : entry=8489
UPDATE `trans_words` SET `content_loc4`='This power cell should hold for our purposes. Once the money starts rolling in, we\'ll improve it even more.$B$BEnough with such things. Every moment spent in discussion translates to lost profit.' WHERE `word_id` = 150369; -- locales_quest - offerrewardtext : entry=10270
UPDATE `trans_words` SET `content_loc4`='This should do the trick. I\'d hoped for a bit more, but to be honest, we were lucky to get our hands on any at all out here.' WHERE `word_id` = 151922; -- locales_quest - offerrewardtext : entry=10667
UPDATE `trans_words` SET `content_loc4`='This should prove a large enough supply for my needs.$B$BI hope the imp didn\'t give you too much trouble. He has a bit of a nasty mouth on him.' WHERE `word_id` = 152944; -- locales_quest - offerrewardtext : entry=10924
UPDATE `trans_words` SET `content_loc4`='This stuff is great! A little bent and a little burnt, but I\'ve never let low grade materials stop me from making precision high explosives.$B$BThanks, $N. You really have an eye for quality trash!' WHERE `word_id` = 149582; -- locales_quest - offerrewardtext : entry=10086
UPDATE `trans_words` SET `content_loc4`='This timeline has been restored and the assault of the Infinite Dragonflight upon Hillsbrad has been quashed.$B$BReturn to Andormu at once. He awaits your arrival in the master\'s lair.' WHERE `word_id` = 150443; -- locales_quest - details : entry=10285
UPDATE `trans_words` SET `content_loc4`='This will help us trade with other sporelings for food and supplies. Sporeggar thanks you, $N!' WHERE `word_id` = 148471; -- locales_quest - offerrewardtext : entry=9808
UPDATE `trans_words` SET `content_loc4`='This will keep us supplied for quite a while, and preparing it for consumption will certainly keep me busy. Thank you for your help, $N.' WHERE `word_id` = 147562; -- locales_quest - offerrewardtext : entry=9580
UPDATE `trans_words` SET `content_loc4`='This will provide you with greater power against the Scourge. Bring more hearts if you find them, $N.' WHERE `word_id` = 146498; -- locales_quest - offerrewardtext : entry=9217
UPDATE `trans_words` SET `content_loc4`='This will surely soften up their defenses and make them think twice before rushing foolishly into our ranks!' WHERE `word_id` = 148211; -- locales_quest - offerrewardtext : entry=9746
UPDATE `trans_words` SET `content_loc4`='This... this pendant. I gave it to Old Whitebark after his people helped us rebuild our village.I guess this means he\'s...<clears her throat as she regains her composure.>I appreciate you bringing this to me, $N. There is something I\'d like to ask of you.' WHERE `word_id` = 146014; -- locales_quest - offerrewardtext : entry=8474
UPDATE `trans_words` SET `content_loc4`='Those are the juiciest mana berries I\'ve ever seen. I think I\'m getting smarter just by looking at them.$B$BSince I no longer need most of my meat and fish stock, why don\'t you take some? I\'m certain you could make something delicious.' WHERE `word_id` = 154251; -- locales_quest - offerrewardtext : entry=11380
UPDATE `trans_words` SET `content_loc4`='Those foul spirits deserved to die. The death they would have wrought on others is reason enough.$B$BWe cannot hope to redeem those in Terokk\'s grasp. But we shall deliver them a devastating blow in the name of the Light.' WHERE `word_id` = 152596; -- locales_quest - offerrewardtext : entry=10848
UPDATE `trans_words` SET `content_loc4`='Those pesky Dark Irons are back! We need to make sure that you can defend yourself if needed. More importantly, we need to know that you can defend our property!$B$BS.T.O.U.T. here is ready for action, are you? Drink from the Complimentary Brewfest Samplers then throw the mug at S.T.O.U.T.! Hit the target 5 times then talk to me.' WHERE `word_id` = 154776; -- locales_quest - details : entry=12191
UPDATE `trans_words` SET `content_loc4`='Those things stink more than I thought they would!$B$BQuickly, hand them over to me and I will imbue them with Nature\'s force such that they will prove extremely deadly to their fel masters and their unnatural creations.' WHERE `word_id` = 152851; -- locales_quest - offerrewardtext : entry=10904
UPDATE `trans_words` SET `content_loc4`='Though it may end up hurting my business in the long run, you look the sort that dreams of flying $G his : her; own mount through the skies, rather than being flown from place to place.$B$BI understand the feeling completely.$B$BYou, my friend, need to head to Shadowmoon Village in the northwest corner of Shadowmoon Valley. When you get there, speak with Olrokk about some flight instruction. Mind you, his training\'s not cheap. But, I think that you\'ll find it worth your while.' WHERE `word_id` = 154412; -- locales_quest - details : entry=11498
UPDATE `trans_words` SET `content_loc4`='Thrall is a most wise and noble leader. His tenacity on the battlefield is matched only by his diplomatic tact.$B$BThe Darkspear Trolls have proven to be invaluable allies to orckind. Out of loyalty and honor we have sworn a pact of protection with them.$B$BThe Warchief has sent orders to make sure our Darkspear friends in Sen\'jin Village are not under any direct threat. One of their most trusted scouts, Lar Prowltusk is keeping a close watch on their western flank.$B$BSeek out Lar and assist as needed.' WHERE `word_id` = 145490; -- locales_quest - details : entry=785
UPDATE `trans_words` SET `content_loc4`='Thrall wants you to return to Gorkan Bloodfist at Mag\'har Post in Hellfire Peninsula and offer him your services.' WHERE `word_id` = 147016; -- locales_quest - objectives : entry=9441
UPDATE `trans_words` SET `content_loc4`='Thrall\'s Destiny Fulfilled' WHERE `word_id` = 150441; -- locales_quest - ObjectiveText1 : entry=10284
UPDATE `trans_words` SET `content_loc4`='Thrallmar has been subjected to bombardment from the Legion Camps since its establishment. We\'ve managed to hold out so far, but the situation is getting more desperate with every day.$B$BShadow Hunter Ty\'jin has made several attempts to learn more about the camps, but none of his scouts ever returned.$B$BThe time for subtlety has passed, $N. Fight your way into Forge Camp: Rage and find out just what the Legion is up to.' WHERE `word_id` = 149596; -- locales_quest - details : entry=10089
UPDATE `trans_words` SET `content_loc4`='Three years I spent in that hell hole. Three years a prisoner of war to the damnable Frostwolf Clan. Made to sit in their tower and listen to them endlessly whine about the bridge to Dun Baldar. About how it\'s "unfair" that blah, blah, blah, blah... War isn\'t fair! To hell with \'em all!$B$BThe minute I was freed I left that place forever and came here to start a new life. Now Mulv and I are vying for the top orc spot. If you want it, you\'re gonna have to go through me, scrub.$B$BWe go when you\'re ready.' WHERE `word_id` = 273681; -- locales_quest - details : entry=11069
UPDATE `trans_words` SET `content_loc4`='Throw caution to the wind.' WHERE `word_id` = 152061; -- locales_quest - EndText : entry=10710
UPDATE `trans_words` SET `content_loc4`='Throw water buckets onto 5 fires, and then speak with the Costumed Orphan Matron.' WHERE `word_id` = 154164; -- locales_quest - objectives : entry=11360
UPDATE `trans_words` SET `content_loc4`='Th-the Blood of Zul\'jin himself you say?$B$B<Gathering himself, Budd continues>$B$BWell, I appreciate the gesture, $C, but I could\'ve taken him down with ease. There\'s no way he was as fearful as the legends say. These things have a way of being utterly overstated, as you\'ll learn in time. More likely, the pitiful old cretin was overcome with arthritis!$B$BAnyway, I suppose it was the least you could do as payment for the kindness I\'ve shown you here.$B$BNow off with you - I must dispose of this vile vial!' WHERE `word_id` = 153950; -- locales_quest - offerrewardtext : entry=11178
UPDATE `trans_words` SET `content_loc4`='Thunderlord Dire Wolf strengthened' WHERE `word_id` = 151235; -- locales_quest - ObjectiveText1 : entry=10488
UPDATE `trans_words` SET `content_loc4`='To be honest, I had expected more, but you\'ll have to do. Welcome to Swamprat Post.' WHERE `word_id` = 149656; -- locales_quest - offerrewardtext : entry=10103
UPDATE `trans_words` SET `content_loc4`='To be my eyes will require some diligence on your part. Nethrandamus will take you through the Legion camps, but you must catalog everything that you see. Even the smallest detail that you may think to be inconsequential could be useful for our purposes.$B$BPrimarily, I want you to note what structures are in the camps and what comprises the camps\' defenses.$B$BNethrandamus is ready when you are, $N. Do not be afraid, he will keep you safe from harm.' WHERE `word_id` = 272827; -- locales_quest - details : entry=9991
UPDATE `trans_words` SET `content_loc4`='To begin your understanding of the Mok\'Nathal, slay Vekh, leader of the Vekh\'nir arakkoa. Then, return to Leoroxx at Mok\'Nathal Village in the Blade\'s Edge Mountains.' WHERE `word_id` = 152585; -- locales_quest - objectives : entry=10846
UPDATE `trans_words` SET `content_loc4`='To make a Twilight Cultist Signet Ring, I need a bunch of items' WHERE `word_id` = 145950; -- locales_quest - details : entry=8339
UPDATE `trans_words` SET `content_loc4`='To the east, at the terminus of Dragons\' End, is the Cursed Hollow. Therein, Grulloc, son of Gruul, plots his dimwitted schemes.$B$BI heard that one of his possessions was stolen from him through trickery. Was that you?  Unfortunate that he was not slain in the process.$B$BIf we are to regain our freedom, I would have you take his most prized possession, a dragon skull from when he rejoiced in his father\'s slaying of the Black Dragonflight. The only way you\'ll get the skull is to vanquish him.' WHERE `word_id` = 153232; -- locales_quest - details : entry=10995
UPDATE `trans_words` SET `content_loc4`='To\'gun was here earlier giving me his report. He should have stayed with you to help!$B$BI sent him back out there to assist Grik\'tha. I think they make a cute couple.$B$BSo, these are the soul devices that the Shadow Council were using to enhance their summoning power? They look dangerous to me' WHERE `word_id` = 149604; -- locales_quest - offerrewardtext : entry=10091
UPDATE `trans_words` SET `content_loc4`='Today I have a buyer who is interested in a piece that\'s held by the mount of Illidan\'s herald. I, of course, speak of Vazruden the Herald and his dragon mount, Nazan.$B$BIf you want the finder\'s fee that I offer in return, find them flying above the Ramparts at Hellfire Citadel and return with the dragon\'s riding crop.$B$BMy sources tell me that the item in question is personally held by the dragon. It is said that Nazan refuses to allow Vazruden to strike him with it.' WHERE `word_id` = 154152; -- locales_quest - details : entry=11354
UPDATE `trans_words` SET `content_loc4`='Today I have a personal request of you, yet the reward will certainly match the danger.$B$BMy family has a \'history\', shall we say, with Nexus-Prince Shaffar and his followers. If the amulet he wears were to fall into my hands then surely that would mean that he had been dealt with.$B$BIf the bounty I am offering is of interest to you then journey to his lair within Auchindoun\'s Mana-Tombs.' WHERE `word_id` = 154214; -- locales_quest - details : entry=11373
UPDATE `trans_words` SET `content_loc4`='Today I have a very wealthy collector in need of something very unique indeed.$B$BWarchief Kargath Bladefist carries upon his person his seal of command, which he uses to sign all official documents. I want you to retrieve it for me.$B$BYou will find him deep within the Shattered Halls. No doubt you will have to wade through a veritable army to get to him. I have no doubt that you will succeed.' WHERE `word_id` = 154179; -- locales_quest - details : entry=11363
UPDATE `trans_words` SET `content_loc4`='Today you\'ve dealt a deadly blow to the Scourge\'s grasp in the region.$B$BWith the vile traitor gone, it shall not be long before we\'ve reclaimed our lands and our greatness is restored once again.' WHERE `word_id` = 146397; -- locales_quest - offerrewardtext : entry=9167
UPDATE `trans_words` SET `content_loc4`='Today\'s client seeks something truly unique.$B$BIt is said that Harbinger Skyriss is a servant of the Old Gods. It is also said that he carries a large scroll that contains their instructions for his domination of life in the universe on their behalf.$B$BMy current buyer wishes to get $G his : her; hands on that scroll, nevermind that $G he : she; won\'t be able to read the language upon it.$B$BTravel to Tempest Keep\'s Arcatraz and get it for me, will you, $C?' WHERE `word_id` = 154289; -- locales_quest - details : entry=11388
UPDATE `trans_words` SET `content_loc4`='Too much idle time for you, $N. It\'s time that you learned more of our trade. Just last night, five of our number were found dead in one of the cracks in Orgrimmar, with few leads. There are those that oppose us, using our own methods against us.$B$BSo it is I\'ve decided you should learn the art of lockpicking.$B$BUnfortunately, it is a skill that my hands are not well suited to. You should seek out Rokar, who lives in the hills near Durotar. Even in his old age, I suspect he still has the touch for it.' WHERE `word_id` = 145557; -- locales_quest - details : entry=2000
UPDATE `trans_words` SET `content_loc4`='Tooki wants you to report to Sergeant Chawni at Stonebreaker Camp in Terokkar Forest.' WHERE `word_id` = 151098; -- locales_quest - objectives : entry=10448
UPDATE `trans_words` SET `content_loc4`='Tor\'chunk Twoclaws has asked you to slay 10 Bloodmaul Maulers and 5 Bloodmaul Warlocks. Afterward, return to him at Thunderlord Stronghold in the Blade\'s Edge Mountains.' WHERE `word_id` = 152320; -- locales_quest - objectives : entry=10784
UPDATE `trans_words` SET `content_loc4`='Tor\'chunk Twoclaws has tasked you with the destruction of any 30 Bladespire Ogres and 10 Bladespire Raptors at Bladespire Hold and the Bladespire Grounds. Return to him at Thunderlord Stronghold in the Blade\'s Edge Mountains once your task is completed.' WHERE `word_id` = 151259; -- locales_quest - objectives : entry=10503
UPDATE `trans_words` SET `content_loc4`='Tor\'chunk Twoclaws wants this tunnel cleared out so we can expedite trade with Swamp Rat. I must not leave my post here so I am leaving the task in your hands. Clear the tunnel and report to Tor\'chunk Twoclaws at once!' WHERE `word_id` = 273561; -- locales_quest - details : entry=10928
UPDATE `trans_words` SET `content_loc4`='Torch catching is a lot of fun, don\'t you think?  But have care!  You don\'t want to get singed!' WHERE `word_id` = 154709; -- locales_quest - offerrewardtext : entry=11657
UPDATE `trans_words` SET `content_loc4`='Torkus has asked you to obtain an Unstable Flask of the Beast for him. Return with it to him at Ogri\'la atop the Blade\'s Edge Mountains.' WHERE `word_id` = 153379; -- locales_quest - objectives : entry=11030
UPDATE `trans_words` SET `content_loc4`='Torkus has asked you to obtain an Unstable Flask of the Sorcerer for him. Return with it to him at Ogri\'la atop the Blade\'s Edge Mountains.' WHERE `word_id` = 153498; -- locales_quest - objectives : entry=11061
UPDATE `trans_words` SET `content_loc4`='Toshley has asked you to take the Protovoltaic Magneto Collector and gather up 5 Electromentals. Return to him at Toshley\'s Station in the Blade\'s Edge Mountains after you have done so.' WHERE `word_id` = 151590; -- locales_quest - objectives : entry=10584
UPDATE `trans_words` SET `content_loc4`='Toshley wants you to retrieve the Collection of Souls and return it to him at Toshley\'s Station in the Blade\'s Edge Mountains.' WHERE `word_id` = 151961; -- locales_quest - objectives : entry=10675
UPDATE `trans_words` SET `content_loc4`='Toshley wants you to use the Repolarized Magneto Sphere to absorb 25 lightning strikes from the Scalewing Serpents. As well, collect 5 Scalewing Lightning Glands. Then, deliver them back to him at Toshley\'s Station in the Blade\'s Edge Mountains.' WHERE `word_id` = 151880; -- locales_quest - objectives : entry=10657
UPDATE `trans_words` SET `content_loc4`='Toshley would like you to use the Light Trap to catch 15 Razaani Light Orbs. Return to him at Toshley\'s Station in the Blade\'s Edge Mountains after you have done so.' WHERE `word_id` = 151955; -- locales_quest - objectives : entry=10674
UPDATE `trans_words` SET `content_loc4`='Toss buckets on 5 fires, then speak with the Masked Orphan Matron in Brill.' WHERE `word_id` = 154357; -- locales_quest - objectives : entry=11449
UPDATE `trans_words` SET `content_loc4`='Toss buckets on 5 fires, then speak with the Masked Orphan Matron in Falconwing Square.' WHERE `word_id` = 154361; -- locales_quest - objectives : entry=11450
UPDATE `trans_words` SET `content_loc4`='Toss buckets on 5 fires, then speak with the Masked Orphan Matron in Razor Hill.' WHERE `word_id` = 154170; -- locales_quest - objectives : entry=11361
UPDATE `trans_words` SET `content_loc4`='Totem Tests Performed' WHERE `word_id` = 153922; -- locales_quest - ObjectiveText1 : entry=11169
UPDATE `trans_words` SET `content_loc4`='Traitors, all of them!$B$BGood work, $C! If you find your way back down there again, I\'m always looking to add to my collection of their insignia!' WHERE `word_id` = 150546; -- locales_quest - offerrewardtext : entry=10308
UPDATE `trans_words` SET `content_loc4`='Travel northwest along the road toward Shattrath and search for evidence of what happened to Deirom.' WHERE `word_id` = 148987; -- locales_quest - objectives : entry=9930
UPDATE `trans_words` SET `content_loc4`='Travel northwest along the road toward Shattrath and search for evidence of what happened to Dugar.' WHERE `word_id` = 148985; -- locales_quest - objectives : entry=9929
UPDATE `trans_words` SET `content_loc4`='Travel south along the west side of the Northern Rampart and search the next camp for signs of Scout Makha' WHERE `word_id` = 149851; -- locales_quest - objectives : entry=10151
UPDATE `trans_words` SET `content_loc4`='Travel south along the west side of the South Rampart and search the next camp for signs of Private Imarion.' WHERE `word_id` = 149757; -- locales_quest - objectives : entry=10126
UPDATE `trans_words` SET `content_loc4`='Travel through the Dark Portal and find Commander Duron. He awaits your arrival on the other side - in the Outland.' WHERE `word_id` = 272922; -- locales_quest - objectives : entry=10119
UPDATE `trans_words` SET `content_loc4`='Travel through the Dark Portal and find Lieutenant General Orion. He awaits your arrival on the other side, in Outland.' WHERE `word_id` = 272426; -- locales_quest - objectives : entry=9407
UPDATE `trans_words` SET `content_loc4`='Travel through the Dark Portal and speak with Nazgrel at Thrallmar.' WHERE `word_id` = 149448; -- locales_quest - objectives : entry=10046
UPDATE `trans_words` SET `content_loc4`='Travel to a capital city and return the Totemic Beacon to the Earthen Ring Elder.' WHERE `word_id` = 154761; -- locales_quest - objectives : entry=12012
UPDATE `trans_words` SET `content_loc4`='Travel to Astranaar in Ashenvale and speak with Vindicator Palais.' WHERE `word_id` = 146968; -- locales_quest - objectives : entry=9432
UPDATE `trans_words` SET `content_loc4`='Travel to Darkshire in Duskwood and speak with Anchorite Delan.' WHERE `word_id` = 146954; -- locales_quest - objectives : entry=9429
UPDATE `trans_words` SET `content_loc4`='Travel to Netherwing Ledge and deliver the Illidari Service Papers to Overlord Mor\'ghor at Dragonmaw Base Camp.' WHERE `word_id` = 273626; -- locales_quest - objectives : entry=11013
UPDATE `trans_words` SET `content_loc4`='Travel to Orgrimmar and give Thrall the Letter from Nazgrel.' WHERE `word_id` = 146998; -- locales_quest - objectives : entry=9438
UPDATE `trans_words` SET `content_loc4`='Travel to Shadra\'Alor and search for traces of Featherbeard.' WHERE `word_id` = 147164; -- locales_quest - objectives : entry=9476
UPDATE `trans_words` SET `content_loc4`='Travel to Shattrath City and speak with Rilak.' WHERE `word_id` = 152788; -- locales_quest - objectives : entry=10889
