UPDATE `trans_words` SET `content_loc3`='Be resolute in your endeavors, $N. The Soulflayer will attempt to siphon your will to live.' WHERE `word_id` = 142751; -- locales_quest - offerrewardtext : entry=8050
UPDATE `trans_words` SET `content_loc3`='Be unstoppable, like the molten fronts of Kajaro.' WHERE `word_id` = 142900; -- locales_quest - offerrewardtext : entry=8102
UPDATE `trans_words` SET `content_loc3`='Be well, $N. If you change your mind, I shall be here to assist you.' WHERE `word_id` = 144267; -- locales_quest - offerrewardtext : entry=8764
UPDATE `trans_words` SET `content_loc3`='Bessy, is that you?$B$BThank you for bringing back my Bessy, $N. I couldn\'t live without her!' WHERE `word_id` = 150686; -- locales_quest - offerrewardtext : entry=10337
UPDATE `trans_words` SET `content_loc3`='Beware the evil Hakkari spirits, mon. Honestly, they be spooky.' WHERE `word_id` = 142937; -- locales_quest - offerrewardtext : entry=8117
UPDATE `trans_words` SET `content_loc3`='Beyond my wildest dreams, I never thought that you would be able to retrieve the crystal!$B$BAnd then to actually give it to me! Do you have any idea how powerful it is?$B$BYour selflessness is inspirational and beyond reproach, $N! You have given me much to think about.' WHERE `word_id` = 150401; -- locales_quest - offerrewardtext : entry=10276
