UPDATE `trans_words` SET `content_loc3`='Bomb parts. So my suspicions were true. That\'s not something that I wanted to be right about.$B$BOkay, you and I have some grim business ahead of us then.' WHERE `word_id` = 151081; -- locales_quest - offerrewardtext : entry=10444
UPDATE `trans_words` SET `content_loc3`='Book of the dead? Never heard of it... What\'s this about anyway? Who sent you?' WHERE `word_id` = 150170; -- locales_quest - offerrewardtext : entry=10228
UPDATE `trans_words` SET `content_loc3`='Borgrim Stouthammer wants you to procure 11 Bloodmaul Brutebane Brew for him. Once you\'ve done that, return it to him at Sylvanaar in the Blade\'s Edge Mountains.' WHERE `word_id` = 151298; -- locales_quest - objectives : entry=10511
