UPDATE `trans_words` SET `content_loc3`='Disrupt Portal Grimh' WHERE `word_id` = 149831; -- locales_quest - ObjectiveText1 : entry=10144
UPDATE `trans_words` SET `content_loc3`='Disrupt Portal Kaalez' WHERE `word_id` = 149832; -- locales_quest - ObjectiveText3 : entry=10144
UPDATE `trans_words` SET `content_loc3`='Dizzy Dina has asked you to acquire 5 Ether-Energized Flesh and return them to her at Toshley\'s Station in the Blade\'s Edge Mountains.' WHERE `word_id` = 151940; -- locales_quest - objectives : entry=10671
UPDATE `trans_words` SET `content_loc3`='Dizzy Dina wants you to kill 10 Ridgespine Stalkers. After you have done so, return to her at Toshley\'s Station in the Blade\'s Edge Mountains.' WHERE `word_id` = 151737; -- locales_quest - objectives : entry=10620
UPDATE `trans_words` SET `content_loc3`='Do not fear, $N. While you are here we will speak using our minds.$B$BI am grateful to Farseer Nobundo for sending you to me. There is a great disturbance that is polluting the waters on and around Bloodmyst Isle. Not even the blind would fail to notice the effect this is having on its creatures and the environment.$B$BYou and I will work together to stop this corruption before it spreads further.' WHERE `word_id` = 147245; -- locales_quest - offerrewardtext : entry=9501
UPDATE `trans_words` SET `content_loc3`='Do not let Lady Prestor\'s protestations distract you, $N. That she is blind to this problem does not surprise me, but that is a separate matter.$B$BAs I was saying, you and Lady Jaina have my thanks for bringing your findings to me in such haste. Let her know that no good citizen of Stormwind will rest until our king walks among us again.$B$BGo to the Mage Quarter at the western end of the city and speak with Archmage Malin at the foot of the tower there. He will speed your return to Theramore.' WHERE `word_id` = 154087; -- locales_quest - details : entry=11223
UPDATE `trans_words` SET `content_loc3`='Do the "train" for Kali Remik' WHERE `word_id` = 143389; -- locales_quest - EndText : entry=8358
UPDATE `trans_words` SET `content_loc3`='Do what you must to defeat our enemies, $C. Even if it means channeling the Light.$B$B<Jin\'rokh shudders.>' WHERE `word_id` = 142743; -- locales_quest - offerrewardtext : entry=8046
UPDATE `trans_words` SET `content_loc3`='Do you know what I hate worse than being wrong? Losing gold on a bet that you couldn\'t pull it off, sweetheart.$B$BOutstanding! With those three dead their forces should be in disarray. And with their artillery up there silenced we\'ll have an easier time getting close enough to take the citadel!$B$BI have a feeling there\'s more to this than meets the eye, though' WHERE `word_id` = 147543; -- locales_quest - offerrewardtext : entry=9575
UPDATE `trans_words` SET `content_loc3`='Do you know what that was? Gnomish engineering at its finest! S.T.O.U.T. took the abuse like a champ!$B$BI think you\'re ready now to take on the Dark Irons if they show up again.' WHERE `word_id` = 154769; -- locales_quest - offerrewardtext : entry=12022
UPDATE `trans_words` SET `content_loc3`='Do you not yet understand? The journey was the destination. Can you not feel the battle scars? The pain you underwent?$B$BHaving survived the ordeal, you are a more powerful being. Much more powerful. This breastplate is merely a reflection of your own innate abilities.' WHERE `word_id` = 140007; -- locales_quest - offerrewardtext : entry=5166
UPDATE `trans_words` SET `content_loc3`='Do you understand what must be done? Find the sons of Oronok. Find my boys...' WHERE `word_id` = 151339; -- locales_quest - offerrewardtext : entry=10519
UPDATE `trans_words` SET `content_loc3`='Does my appearance startle you? Do you know what I am?' WHERE `word_id` = 149181; -- locales_quest - offerrewardtext : entry=9982
UPDATE `trans_words` SET `content_loc3`='Draenei Survivors Saved' WHERE `word_id` = 146607; -- locales_quest - ObjectiveText1 : entry=9283
UPDATE `trans_words` SET `content_loc3`='Dragonmaw Peon Camp Poisoned' WHERE `word_id` = 153337; -- locales_quest - ObjectiveText1 : entry=11020
UPDATE `trans_words` SET `content_loc3`='Dragonmaw Peon Disciplined' WHERE `word_id` = 153472; -- locales_quest - ObjectiveText1 : entry=11055
UPDATE `trans_words` SET `content_loc3`='Draw from the darkness, $C. Embrace the shadows. Let Kezan\'s Taint supplement your dark meddling.' WHERE `word_id` = 142915; -- locales_quest - offerrewardtext : entry=8107
UPDATE `trans_words` SET `content_loc3`='Dreadlords!  They plan to attack us here at Splintertree Post?!$B$BNot while I still draw breath!$B$B<Valusha crumples the demon-scrawled plans in her fist.>$B$BNo. We will not allow this to happen!  They are a blight upon our world!' WHERE `word_id` = 147386; -- locales_quest - offerrewardtext : entry=9535
UPDATE `trans_words` SET `content_loc3`='Drek\'Thar sings your praises. Kalimdor is abuzz with tales of your heroics. The Warchief glows - your stalwart defense of our clan has pleased him greatly.$B$BYou have earned the Eye of Command.$B$BPresent your insignia, Commander $N.$B$B<Warmaster Laggrond salutes>' WHERE `word_id` = 141916; -- locales_quest - details : entry=7167
UPDATE `trans_words` SET `content_loc3`='Durn\'s murderous grip has been loosed! Nagrand has a brighter tomorrow because of you, hero. Well done!' WHERE `word_id` = 149020; -- locales_quest - offerrewardtext : entry=9937
UPDATE `trans_words` SET `content_loc3`='Earthbinder Rayge Discovered' WHERE `word_id` = 148177; -- locales_quest - ObjectiveText1 : entry=9738
UPDATE `trans_words` SET `content_loc3`='Earthen Soul Captured' WHERE `word_id` = 151136; -- locales_quest - ObjectiveText1 : entry=10458
UPDATE `trans_words` SET `content_loc3`='Eastern Cannon Burned' WHERE `word_id` = 149589; -- locales_quest - ObjectiveText1 : entry=10087
