UPDATE `trans_words` SET `content_loc3`='Eastern Thrower Burned' WHERE `word_id` = 149549; -- locales_quest - ObjectiveText1 : entry=10078
UPDATE `trans_words` SET `content_loc3`='Eavesdrop on the Bloodmaul ogres\' plans' WHERE `word_id` = 152084; -- locales_quest - ObjectiveText1 : entry=10714
UPDATE `trans_words` SET `content_loc3`='Elder Ungriz at the Mag\'hari Procession in Nagrand has asked that you free 15 Mag\'har Prisoners.' WHERE `word_id` = 149058; -- locales_quest - objectives : entry=9948
UPDATE `trans_words` SET `content_loc3`='Elder Yorley at the Mag\'hari Procession in Nagrand has asked that you slay 15 Warmaul Brutes and 15 Warmaul Warlocks.' WHERE `word_id` = 149046; -- locales_quest - objectives : entry=9945
UPDATE `trans_words` SET `content_loc3`='Electromentals collected' WHERE `word_id` = 151593; -- locales_quest - ObjectiveText1 : entry=10584
