UPDATE `trans_words` SET `content_loc9`='Can you not sense the anguish of the Earth Mother for her lost children, the undead? Speak quickly, $N. Every second costs me dearly. We must help them find their way back to her!' WHERE `word_id` = 193647; -- locales_npc_text - Text0_1 : entry=5080
UPDATE `trans_words` SET `content_loc9`='Can Zamja help you, $R?' WHERE `word_id` = 194034; -- locales_npc_text - Text0_1 : entry=5799
