UPDATE `trans_words` SET `content_loc9`='Careful, don\'t get burned!' WHERE `word_id` = 196822; -- locales_npc_text - Text1_0 : entry=12506
UPDATE `trans_words` SET `content_loc9`='Certainly, $G sir:miss;. We were once servants of the house Barov: What is now known as the Scholomance.$B$BI was the head maid and Lucien was Master Barov\'s butler.$B$BWe had worked for the Barovs for decades, until...$B$B<Eva weeps.>' WHERE `word_id` = 193142; -- locales_npc_text - Text0_1 : entry=4042
UPDATE `trans_words` SET `content_loc9`='Certainly, $N. You only need to tell me what reputation you would like to know about.' WHERE `word_id` = 195213; -- locales_npc_text - Text0_1 : entry=8498
UPDATE `trans_words` SET `content_loc9`='Certainly, $N.$B$BAdventurers that the Dawn views as exalted must submit a combined total of 12 insignias to access superior items. To access items of even greater power, they would be required to submit a total of 54 insignias.$B$BThose exalted in the eyes of the Dawn receive the greatest discounts.' WHERE `word_id` = 195217; -- locales_npc_text - Text0_1 : entry=8502
