UPDATE `trans_words` SET `content_loc9`='Certainly, $N.$B$BAdventurers that the Dawn views as revered must submit a combined total of 14 insignias to access superior items. To access items of even greater power, they would be required to submit a total of 90 insignias.$B$BThose that are revered by the Dawn will find the cost of superior items quite reasonable! The cost for the more powerful items is still high.' WHERE `word_id` = 195215; -- locales_npc_text - Text0_1 : entry=8500
UPDATE `trans_words` SET `content_loc9`='Champion $N, it is good to see you again.$B$BHave you returned to Valhalas to assist a friend with their challenges?' WHERE `word_id` = 197604; -- locales_npc_text - Text0_1 : entry=13946
UPDATE `trans_words` SET `content_loc9`='Cherry pie is my favoritist food in all of Azeroth.' WHERE `word_id` = 192486; -- locales_npc_text - Text7_0 : entry=2355
UPDATE `trans_words` SET `content_loc9`='Close, but no cigar! I\'m afraid you\'ll have to talk to my sis, Jemma first. After she\'s gone over the basics with you, come see me and I\'ll show you the juicy stuff!' WHERE `word_id` = 193714; -- locales_npc_text - Text0_1 : entry=5155
UPDATE `trans_words` SET `content_loc9`='Clothing!?  That won\'t help you against good steel!  Bah, if you\'re sure you want to be a tailor go find Magar\'s Cloth Goods in The Drag.' WHERE `word_id` = 192526; -- locales_npc_text - Text1_0 : entry=2518
UPDATE `trans_words` SET `content_loc9`='Come and take a look at my wares while you still can. We\'re here all week, and then we\'re off to some far off land.' WHERE `word_id` = 194588; -- locales_npc_text - Text2_1 : entry=7389
UPDATE `trans_words` SET `content_loc9`='Come closer, young one; my ears aren\'t what they once were, and I care not for raising my voice.' WHERE `word_id` = 197141; -- locales_npc_text - Text0_1 : entry=11691
UPDATE `trans_words` SET `content_loc9`='Come for some blacksmithing supplies, have ya?' WHERE `word_id` = 195454; -- locales_npc_text - Text0_1 : entry=9019
