UPDATE `trans_words` SET `content_loc9`='Did you see?  That last firework almost caught that dwarf\'s shirt on fire!  Hah!' WHERE `word_id` = 195063; -- locales_npc_text - Text1_0 : entry=8221
UPDATE `trans_words` SET `content_loc9`='Didn\'t we just break free of the Burning Legion, and here you are messing with demons again?  Seek out the Darkfire Enclave in the underground cavern known as the Cleft of Shadow. You\'ll find a tunnel down into it from The Drag.' WHERE `word_id` = 192550; -- locales_npc_text - Text1_0 : entry=2564
UPDATE `trans_words` SET `content_loc9`='Dis be horrible, mon. I cannot bear to be watchin\'.' WHERE `word_id` = 199417; -- locales_npc_text - Text1_0 : entry=13848
UPDATE `trans_words` SET `content_loc9`='Do Great Ones ever run out of bag space for shinies?' WHERE `word_id` = 199658; -- locales_npc_text - Text1_0 : entry=14535
UPDATE `trans_words` SET `content_loc9`='Do I know you?' WHERE `word_id` = 132023; -- locales_npc_text - Text1_0 : entry=11287
UPDATE `trans_words` SET `content_loc9`='Do I look to you like I cast spells?  Well, I did hear that Cain Firesong was hanging out on the upstairs landing of the Gallows\' End Tavern at the southeast end of town. Odd how he just stands there outside of Gretchen\'s room like that.' WHERE `word_id` = 193194; -- locales_npc_text - Text1_0 : entry=4077
UPDATE `trans_words` SET `content_loc9`='Do not bother me, pest. I am studying the inept fighting of these so-called combatants.$B$BI will best them all, and then I will be elevated to serve the Lich King.' WHERE `word_id` = 199364; -- locales_npc_text - Text0_1 : entry=13678
UPDATE `trans_words` SET `content_loc9`='Do not fight the enemy unprepared, $N. I can provide you with very powerful items... provided you have proven yourself to us.' WHERE `word_id` = 196802; -- locales_npc_text - Text0_1 : entry=12338
