UPDATE `trans_words` SET `content_loc9`='Greetings, $C. I can assist you in recovering lost companions.' WHERE `word_id` = 196663; -- locales_npc_text - Text0_1 : entry=13584
UPDATE `trans_words` SET `content_loc9`='Greetings, $C. I can help stable your pets or assist you in recovering lost companions.' WHERE `word_id` = 196664; -- locales_npc_text - Text0_1 : entry=13557
