UPDATE `trans_words` SET `content_loc9`='Never leave home without ample supplies of flash powder, especially when working with others. I guarantee someone will run out every time.$B$BWhen the choice comes down to handing out materials or letting the poor sap die, I help... most of the time.' WHERE `word_id` = 196386; -- locales_npc_text - Text4_1 : entry=10686
