UPDATE `trans_words` SET `content_loc6`='Baron Sablemane has tasked you and your friends with the deaths of 10 Bladespire Enforcers, 5 Bladespire Battlemages, 5 Bladespire Ravagers and their leader, Fingrom. When you have managed this feat, return to him at the Circle of Blood in the Blade\'s Edge Mountains.' WHERE `word_id` = 152414; -- locales_quest - objectives : entry=10805
