UPDATE `trans_words` SET `content_loc6`='Sky Sergeant Vanderlip has tasked you with the bombing of 15 Fel Cannonball Stacks. Return to her at the Skyguard Outpost atop the Blade\'s Edge Mountains once you have done so.' WHERE `word_id` = 153297; -- locales_quest - objectives : entry=11010
